import React, { useState, useEffect } from 'react';
import {Link} from "react-router-dom";

import { retrieveFeaturedMedia, retrieveAuthor } from '../../../service/WPService';
import styled, { css } from "styled-components";
import {Title} from '../../Styled';

const Item = styled.section`
    position:relative;
    height:25em;
    max-height:100%;
    width:100%;
    max-width:14em;
    transform-style: preserve-3d;
    ${props => props.flip && css`
        transform: rotateY(180deg);
    `}
`

const Front = styled.div`
    position:absolute;

    backface-visibility: hidden;
    transition: all .5s ease;
    height:100%;
    width:100%;

    background-color:var(--p-1);
    border-radius:1em;
    padding:.5em;
    -webkit-box-shadow: 2px 5px 10px 0px var(--p-vdark);
    -moz-box-shadow: 2px 5px 10px 0px var(--p-vdark);
    box-shadow: 2px 5px 10px 0px var(--p-vdark);
`

const Back = styled.div`
    position:absolute;
    backface-visibility: hidden;
    height:100%;
    width:100%;
    transform: rotateY(180deg);
`

const ItemTitle = styled(Title)`
    padding:0 .5em;
    font-size:1.5em;
    color: var(--p-9);
    ::after, ::before{
        background-color:var(--p-9);
    }
`

const Info = styled.div`
    padding:1em 0;
    height:50%;
    display:flex;
    flex-direction:column;
    justify-content:space-between;
    align-items:center;
`

const Image = styled.img`
    width:100%;
    height:50%;
    object-fit: cover;
    display:block; 
    border-radius:.5em;
`

const Buttons = styled.div`
    display:flex;
    width:100%;
`

const PrimaryButton = styled(Link)`
    background-color: var(--s-3);
    color: var(--p-10);
    display: inline-block;
    text-align: center;
    text-decoration: none;
    border:1px solid var(--s-3);
    border-radius:1em;
    width: 90%;
    padding:.5em;
    margin:0 .5em;
    transition: .5s;
    :hover{
        border:1px solid var(--p-9);
        color: var(--p-9);
    }
`

const SecondaryButton = styled(PrimaryButton)`
    background-color: transparent;
    color: var(--p-10);
    border:1px solid var(--p-9);
    :hover{
        background-color: transparent;
        border:1px solid var(--s-3);
        color: var(--s-3);
    }
`

export default function PostSliderItem(props)  {
    const [post] = useState(props.post);
    const [imageUrl, setImageUrl] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);
    const [flip, setFlip] = useState(false);


    useEffect( () => {
        console.log(post)
        const getImageUrl = retrieveFeaturedMedia(post.featured_media)
        const getAuthor = retrieveAuthor(post.author)
        Promise.all([getImageUrl, getAuthor]).then(res => {
            if(!(res[0] === null)){
                setImageUrl(res[0].media_details.sizes.full.source_url)
            }
            setIsLoaded(true)
        });
    },[post])

    if(isLoaded){
        return (
            <Item>
                <Front>
                    <Image src={imageUrl} alt={post.title.rendered}/>
                    <Info>
                        <ItemTitle dangerouslySetInnerHTML={{__html: post.title.rendered}}/> 
                        <Buttons>
                            <SecondaryButton to="" onClick={() =>{ setFlip(!flip)}}>
                                excerpt
                            </SecondaryButton>
                            <PrimaryButton to={{
                                pathname: `/post/${post.id}`
                            }}>
                                read
                            </PrimaryButton>  
                        </Buttons>
                    </Info> 
                </Front>
                <Back>
                </Back>
            </Item>

        )
    }
    else {
        return (null);
    }
}
