import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import "../App.css";
import {Link} from "react-router-dom";

export class PostItem extends Component {

    state =Â {
        imageUrl: '',
        author: '',
        isLoaded: false
    }

    static propTypes = {
        post: PropTypes.object.isRequired,
    }

    componentDidMount(){
        const { featured_media, author } = this.props.post;
        const getImageUrl = axios(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios(`/wp-json/wp/v2/users/${author}`);
        Promise.all([getImageUrl, getAuthor]).then(res => {
            this.setState({
                imageUrl: res[0].data.media_details.sizes.full.source_url,
                author: res[1].data.name,
                isLoaded: true
            })
        });

    }
    render() {
        const { title, excerpt}  = this.props.post;
        const { imageUrl, isLoaded} = this.state;
        if(isLoaded){
            return (
                <div class="post-item">
                    <div class="square"></div> 
                    <div class="img">
                        <img src={imageUrl} alt={title.rendered}/>
                    </div>
                    <div class="info">
                        <div class="title">
                            <h5>{title.rendered}</h5>
                        </div>
                        <div class="excerpt">
                            <div dangerouslySetInnerHTML={{__html: excerpt.rendered }}/>
                        </div>
                        <Link to={{
                            pathname: "/post"
                        }}>
                        read more 
                        </Link>
                    </div>
                </div>
            )
        }
        return null;
    }
}

export default PostItem
