import React, { Component } from 'react';
import {retrieveNavbar} from '../../service/WPService';
import "../../App.css";
import {Wrapper} from '../Styled'
import styled, {css} from 'styled-components'
import ReorderIcon from '@material-ui/icons/Reorder'

const TopNav = styled.div`
    display:flex;
    margin-bottom: 5rem;
    @media (max-width: 42em) {
        flex-direction:column;
        margin-bottom: 1rem;
    }
`

const NavLink = styled.a`
    text-align: center;
    transition: all 0.3s ease;
    color: var(--primary);
    border: 2px solid var(--primary);
    text-decoration: none;
    margin:.5em;
    padding: .5em;
    width: 8em;
    :hover{
        color: #000;
        background-color:var(--secondary);
        transition:1s;
        -webkit-transition:1s;
        border-radius: 6px;
    }
    @media (max-width: 42em) {
        width:100%;
    }
`
const Logo = styled(NavLink)`
    margin-right: auto;
    @media (max-width: 42em) {
        width:8em;
    }
`

const Right = styled.div`
display:flex;
flex-direction:row;
margin-left:auto;
@media (max-width: 42em) {
    flex-direction:column;
    margin-left:0;
    ${props => props.show && css`
    display:none;
`}
}
`
const Left = styled.div`
    display:flex;
    justify-content:space-between;
`

const Burger = styled.a`
    color:var(--secondary600);
    display:none;
    text-align: center;
    transition: all 0.3s ease;
    text-decoration: none;
    margin:.5em;
    padding: .5em;
    @media (max-width: 42em) {
        display:inline;
    }
`
export default function CommentItem (props) {
    const [listItems, setListItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [clicked, setClicked] = useState(false);

    useEffect( () => {
        retrieveNavbar()
        .then(res => {
            const Items = res.items.map((item) =>
                <NavLink key={item.title} href={item.url}>{item.title}</NavLink>
            );
            setListItems(Items)
        });
    })

    showLinks = () => {
        let show = this.state.clicked;
        this.setState({
            clicked : !show,
        })
    }
        return (
            <Wrapper>
                <TopNav>
                    <Left>
                        <Logo mr href='/'>Logo</Logo>
                        <Burger onClick={this.showLinks}><ReorderIcon/></Burger>
                    </Left>
                    <Right show={this.state.clicked}>
                        {this.state.listItems}
                    </Right>
                </TopNav>
            </Wrapper>

        )
    }
