import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import "../App.css";
import {Link} from "react-router-dom";
import styled from "styled-components";

const Square = styled.div`
    width: 10em;
    height:25em;
    background-color: var(--primary);
    transform: translate(0em, 5em);
`
const Info = styled.div`
    width: 25em;
    height: 25em;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    background-color: white;
    border:2px solid var(--primary);
    transform: translate(-10em, 7em);
`
const ImageContainer = styled.div`
    max-width: 500px;
    transform: translate(-5em, 0em);
`
const Image = styled.img`
    width:20em;
    height:25em;
    object-fit: cover;
    display:block;
`
const Item = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin: 0em 0em 10em 0em;
    :nth-child(even) ${Square}{
        order:3;
        transform: translate(0em, -5em);
    }
    :nth-child(even) ${ImageContainer}{
        transform: translate(5em, 0em);
        order:2;
    }
    :nth-child(even) ${Info}{
        transform: translate(10em, 7em);
    }
`
const Titel = styled.h5`
    text-align: center;
    padding: .5em;
`
const Excerpt = styled.p`
    padding: 1em;
`

export class PostItem extends Component {

    state =Â {
        imageUrl: '',
        author: '',
        isLoaded: false
    }

    static propTypes = {
        post: PropTypes.object.isRequired,
    }

    componentDidMount(){
        const { featured_media, author } = this.props.post;
        const getImageUrl = axios(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios(`/wp-json/wp/v2/users/${author}`);
        Promise.all([getImageUrl, getAuthor]).then(res => {
            this.setState({
                imageUrl: res[0].data.media_details.sizes.full.source_url,
                author: res[1].data.name,
                isLoaded: true
            })
        });
    }

    render() {
        const { title, excerpt}  = this.props.post;
        const { imageUrl, isLoaded} = this.state;
        if(isLoaded){
            return (
                <Item>
                    <Square/> 
                    <ImageContainer>
                        <Image src={imageUrl} alt={title.rendered}/>
                    </ImageContainer>
                    <Info>
                        <Titel>
                            {title.rendered}
                        </Titel>
                            <Excerpt dangerouslySetInnerHTML={{__html: excerpt.rendered.substring(0,300)+'...' }}/>
                        <Button></Button>
                    </Info>
                </Item>
            )
        }
        return null;
    }
}

export default PostItem
