import React from 'react'
import styled from 'styled-components'

export const Comment = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:1em;
    margin:1em;
    display:flex;
    max-width:55em;
    justify-content:space-between;
`

export const Replies = styled.div`
    display:none;
`

export default function CommentItem (props) {
    const [hideReplies, setHideReplies] = useState(true);

    const showReplies = () => {
        axios({
            method: 'get',
            url: `/wp-json/wp/v2/comments/?post=${props.id}&per_page=10`,
            headers: {'Content-Type': 'application/json'}
        })
        .then(res => {
            console.log(res.data)
            setComments(res.data)
            setIsLoaded(true)
        })
        .catch(err => console.error(err))
        setHideReplies(!hideReplies);
    }

    return (
        <div >
            <Comment dangerouslySetInnerHTML={{__html: props.comment.content.rendered}}>
            </Comment>
            <Button onClick={showReplies()}> show replies </Button>
            <Replies>

            </Replies>
        </div>
    )
}
