import React from 'react'
import styled, {keyframes, css} from "styled-components";

const roll = keyframes`
    0% {
        transform:translateX(0px) rotateZ(0deg) ;
    }

    30% {
        transform:translateX(600px) rotateZ(120deg) ;
    }
    60% {
        transform:translateX(1800px) rotateZ(360deg) ;
    }
    100% {
        transform:translateX(1200px) rotateZ(240deg) ;
    }



`;

const Container = styled.div`
    height:800px;
    width:800px;
    position:relative;
    font-size:100px;

    --X:133px;
    --Y:35px;

    --delay0: 0s;
    --delay1: 0s;
    --delay2: .3s;
    --delay3: .6s;
    --delay4: .9s;
    --delay5: 1.2s;

    animation:${roll} 5s 0s linear forwards;
`

const boxTransform1 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;

const boxTransform2 = keyframes`
    0% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
    100% {
        transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
    }
`;

const boxTransform3 = keyframes`
    0% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
    50% {
        transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
    }
    100% {
        transform:translateY(0) translateX(0) skew(0) rotateX(0);
    }
`;

const boxTransform4 = keyframes`
    0% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
    33% {
        transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
    }
    66% {
        transform:translateY(0) translateX(0) skew(0) rotateX(0);
    }
    100% {
        transform:translateY(.92em) translateX(-.24em) skew(-30deg) rotateX(-30deg);
    }
`;

const boxTransform5 = keyframes`
    0% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
    25% {
        transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
    }
    50% {
        transform:translateY(0) translateX(0) skew(0) rotateX(0);
    }
    75% {
        transform:translateY(.92em) translateX(-.24em) skew(-30deg) rotateX(-30deg);
    }
    100% {
        transform:translateY(1.59em) translateX(-.91em) skew(-60deg) rotateX(-60deg);
    }
`;

const fadeInRow = keyframes`
    0% {
        transform:rotateZ(0deg) translateY(var(--Y)) translateX(var(--X));
    }
    100% {
    }
`;

const Box = styled.div`
    opacity: .5;
    height:.99em;
    width:.99em;
    position:absolute;
    border:solid 1px black;
    background-color:${props => props.color};
    background: linear-gradient(90deg,
                                ${props => props.color} 0%, 
                                var(--p-3) 130%);
    transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    ${props => props.order === "1" && css`
        animation:${boxTransform1} var(--delay1) 1s ease-in forwards;
    `}
    ${props => props.order === "2" && css`
        animation:${boxTransform2} var(--delay2) 1s ease-in forwards;
    `}
    ${props => props.order === "3" && css`
        animation:${boxTransform3} var(--delay3) 1s ease-in forwards;
    `}
    ${props => props.order === "4" && css`
        animation:${boxTransform4} var(--delay4) 1s ease-in forwards;
    `}
    ${props => props.order === "5" && css`
        animation:${boxTransform5} var(--delay5) 1s ease-in forwards;
    `}
`

const Box1 = styled(Box)`
    opacity: .5;
`

const Box2 = styled(Box)`
`

const Box3 = styled(Box)`
`

const Box4 = styled(Box)`
`
const Box5 = styled(Box)`
`

const Row = styled.div`
    top: 50%;
    left: 50%;
    position: absolute;
    animation:${fadeInRow} 1s ease-out forwards;
`
const Row1 = styled(Row)`
    transform:rotateZ(0deg) translateY(var(--Y)) translateX(var(--X));
`
const Row2 = styled(Row)`

    transform:rotateZ(-60deg) translateY(var(--Y)) translateX(var(--X));
`

const Row3 = styled(Row)`

    transform:rotateZ(-120deg) translateY(var(--Y)) translateX(var(--X));
`

const Row4 = styled(Row)`

    transform:rotateZ(-180deg) translateY(var(--Y)) translateX(var(--X));
`

const Row5 = styled(Row)`

    transform:rotateZ(-240deg) translateY(var(--Y)) translateX(var(--X));
`

const Row6 = styled(Row)`

    transform:rotateZ(-300deg) translateY(var(--Y)) translateX(var(--X));
`

export default function Figure() {
    return (
        <Container>
            <Row1>
                <Box color="var(--s-5)" order="1" />
                <Box color="var(--s-5)" order="2"/>
                <Box3 color="var(--s-5)" order="3"/>
                <Box4 color="var(--s-5)" order="4"/>
                <Box5 color="var(--s-5)" order="5"/>
            </Row1>
            <Row2>
                <Box1 color="var(--s-7)" order="1"/>
                <Box2 color="var(--s-7)" order="2"/>
                <Box3 color="var(--s-7)" order="3"/>
                <Box4 color="var(--s-7)" order="4"/>
                <Box5 color="var(--s-7)" order="5"/>
            </Row2>
            <Row3>
                <Box1 color="var(--s-5)" order="1"/>
                <Box2 color="var(--s-5)" order="2"/>
                <Box3 color="var(--s-5)" order="3"/>
                <Box4 color="var(--s-5)" order="4"/>
                <Box5 color="var(--s-5)" order="5"/>
            </Row3>
            <Row4>
                <Box1 color="var(--s-3)" order="1"/>
                <Box2 color="var(--s-3)" order="2"/>
                <Box3 color="var(--s-3)" order="3"/>
                <Box4 color="var(--s-3)" order="4"/>
                <Box5 color="var(--s-3)" order="5"/>
            </Row4>
            <Row5>
                <Box1 color="var(--s-1)" order="1"/>
                <Box2 color="var(--s-1)" order="2"/>
                <Box3 color="var(--s-1)" order="3"/>
                <Box4 color="var(--s-1)" order="4"/>
                <Box5 color="var(--s-1)" order="5"/>
            </Row5>
            <Row6>
                <Box1 color="var(--s-3)" order="1"/>
                <Box2 color="var(--s-3)" order="2"/>
                <Box3 color="var(--s-3)" order="3"/>
                <Box4 color="var(--s-3)" order="4"/>
                <Box5 color="var(--s-3)" order="5"/>
            </Row6>
        </Container>
    )
}
