import React from 'react'
import {Wrapper, Paragraph, H4} from '../Styled'
import styled from 'styled-components'
import MinimalIcon from '@material-ui/icons/FiberManualRecord';
import MobileIcon from '@material-ui/icons/PhoneIphone';
import ComponentIcon from '@material-ui/icons/Cached';

const CardContainer = styled(Wrapper)`
    width: 100%;
    display:flex;
    flex-direction:row;
    background: linear-gradient(var(--p-9), #9198e5);
`

const ValueCard = styled.div`
    display:flex;
    flex-direction:column;
    align-items:center;
    height:20em;
    width:15em;
` 

const Icon = styled.div`
    display:flex;
    justify-content:center:
    align-items:center;
    height:5em;
`

export default function Values() {
    return (
        <CardContainer>
            <ValueCard>
                <Icon><MinimalIcon  style={{ fontSize: 40, color: "var(--p-5)" }}/></Icon>
                <H4>minimal</H4>
                <Paragraph fadeIn={true}>
                    Less but Better in the Age of distraction.
                </Paragraph>
            </ValueCard>
            <ValueCard>
                <Icon><MobileIcon style={{ fontSize: 40, color: "var(--p-5)" }}/></Icon>
                <H4>mobile first </H4>
                <Paragraph>
                    Designed for optimal mobile experience, but adaptive to desktop views.
                </Paragraph>
            </ValueCard>
            <ValueCard>
                <Icon><ComponentIcon style={{ fontSize: 40, color: "var(--p-5)" }}/></Icon>
                <H4>component based </H4>
                <Paragraph>
                    Component Based Architecture for consistancy and quick design adaptions.
                </Paragraph>
            </ValueCard>
        </CardContainer>
    )
}
