import React, {Component} from 'react';
import axios from 'axios';
import {Wrapper} from './Styled'

export class PostPage extends Component {

    state =Â {
        post: '',
        imageUrl: '',
        isLoaded: false
    }

    componentDidMount(){        
        const { id } = this.props.match.params;
        console.log(id);

        axios(`/wp-json/wp/v2/posts/${id}`)
        .then(res => {
            console.log(res.data);
            this.setState({
                post: res.data,
            })
            return axios(`/wp-json/wp/v2/media/${res.data.featured_media}`)
        })
        .then(res => {
            console.log(res.data);
            this.setState({
                imageUrl : res.data.media_details.sizes.full.source_url,
                isLoaded: true
            })

        })
        .catch(err => console.error(err))
    }

    render(){ 
        const { post, isLoaded, imageUrl } = this.state;
        if(isLoaded){
            console.log('post');
            console.log(post);
            return(
            <Wrapper>
                <div className="post-page">
                    <div  className="image">
                        <img src={imageUrl}  alt="warum"/>
                    </div>
                    <div className="post" dangerouslySetInnerHTML={{__html: post.content.rendered}}>
                    </div>
                    <p>comments</p>
                </div>
            </Wrapper>
            )
        } else {
            return "loading..."
        }
    }
  }

export default PostPage
