import React from 'react'
import {Comment} from './Styled'

export default function CommentItem ({comments}) {
    return (
        <Comment >
            <li>
            <div><p>'id':{comments[0].id}</p>
            <p>'parent':{comments[0].parent}</p></div>
            </li>
            {comments.forEach((comment, index)=> {
                if(comment.parent==comments[0].id){
                    <CommentItem key={comment.id} comments={comments.slice(index)} ></CommentItem>
                }
            })
            }
        </Comment>
    )
}
