import {useState, useEffect} from 'react'
import CommentItem from './CommentItem'
import CommentForm from './CommentForm'
import {retrieveComments} from '../../service/WPService'
import styled, {css} from 'styled-components'

const CommentSection = styled.section`

`

const Buttons = styled.div`
    display:flex;
    justify-content:space-between;
    p{
        margin:0;
    }
`

const Button = styled.button`
    border: none;
    background-color: transparent;
    color: var(--secondary)
`

const Container = styled.section`
    ${props => props.depth === 1 && css`
        margin-left: 1em;
    `}
`

export default function Comments(props) {

    const [comments, setComments] = useState([])
    const [loaded, setLoaded] = useState(false)
    const [id]  = useState(props.post)
    const [parent] = useState(props.parent)
    const [updated, setUpdated] = useState(false);

    const [hideForm, setHideForm] = useState(false);
    const [hideReplies, setHideReplies] = useState(false);

    const [depth] = useState(props.depth)

    useEffect( () => {

        retrieveComments(id, parent).then( res =>
            setComments(res),
            setLoaded(true),
            setUpdated(false)
        )

        return function cleanup() {
            setLoaded(false);
        }

    }, [id, parent, updated])

    const showReplies = () => {
        setHideReplies(!hideReplies);
    }

    const showForm = () => {
        setHideForm(!hideForm);
    }

        if(loaded){
            return (
                <CommentSection>
                    <Buttons>
                        {depth===1 && <Button onClick={() => showReplies()}>Show replies</Button> }
                        {depth===1 && <Button onClick={() => showForm()}>reply</Button>}
                    </Buttons>
                    {(depth===0||hideForm) && 
                        <CommentForm
                            updated={a => setUpdated(a)} 
                            post={id}
                            parent={parent} 
                            key={'commentForm'+{parent}} 
                        />
                    }
                    <Container depth={depth}>
                        {(depth===0||hideReplies) && comments.map(comment => (
                            <CommentItem 
                                depth={depth+1}
                                key={comment.id} 
                                comment={comment}
                            />
                        ))}
                    </Container>
                </CommentSection>
            )
        } else {
            return 'loading...'
        }
}
