import React, {useState} from 'react'

import {Wrapper} from '../components/Styled';

import styled from "styled-components";

import School from '@material-ui/icons/School';

import Construction from '@material-ui/icons/Build';

import Camera from '@material-ui/icons/CameraAlt';



const Container = styled(Wrapper)`
    min-height:70vh;
    justify-content:flex-start;
    align-items:center;
`

const Item = styled.div`
    width:100%;
    max-width:30em;
    margin:.2em;
`

const TopBar = styled.div`
    height:48px;

    background-color:var(--p-1);
    display:flex;
    align-items:center;

    border-radius: 24px 0 0 24px;
`

const Title = styled.h5`
    color: var(--p-10);
    margin:0;
    padding-left:8px;
`

const Circle = styled.div`
    height:40px;
    width:40px;
    margin:4px;
    background-color:var(--s-3);
    border-radius:50%;
    display:flex;
    justify-content:center;
    align-items:center;
`

const Info = styled.div`
    max-width:100%;
    background-color:white;
    min-height:100px;
    margin: 0 0 0 24px;
    border-left: 2px solid var(--s-3);
`
export default function Lebenslauf() {
    const [active, setActive] = useState(2);

    return (
        <Container>
            <Item>
                <TopBar onClick={()=>setActive(1)}>
                    <Circle>
                        <School/>
                    </Circle>
                    <Title>Education</Title>
                </TopBar>
                {active===1 &&
                    <Info>expand</Info>
                }
            </Item>
            <Item>
                <TopBar onClick={()=>setActive(2)}>
                    <Circle>
                        <Construction/>
                    </Circle>
                    <Title>Skills</Title>
                </TopBar>
                {active===2 &&
                    <p>expand</p>
                }
            </Item>
            <Item>
                <TopBar onClick={()=>setActive(3)}>
                    <Circle>
                        <Camera/>
                    </Circle>
                    <Title>Interests</Title>
                </TopBar>
                {active===3 &&
                    <p>expand</p>
                }
            </Item>
        </Container>
    )
}
