import React from 'react'
import {Comment} from '../Styled'

export default function CommentItem (props) {
    console.log(props.comment);    
    return (
        <div >
            <Comment dangerouslySetInnerHTML={{__html: props.comment.content.rendered.substring(0,300)+'...' }}>
                </Comment>
            <ul>
                {props.children}
            </ul>
        </div>
    )
}
