import {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios';


const Form = styled.form`
display:flex;
flex-direction:column;
`

export default function CommentForm(props) {

    const [state, setState] = useState({
        post:props.post.id,
        name:'',
        email:'',
        content:'',
    })



    function handleSubmit(){
        console.log(state)
        const data = JSON.stringify({
            post: state.post,
            author_name: state.name,
            author_email: state.email,
            content: state.content,
          });
        axios({
            method: 'post',
            url: `/wp-json/wp/v2/comments`,
            headers: {}, 
            data: data
        })
        .catch(err => console.error(err.message))

      }

    return (
        <Form onSubmit={handleSubmit()}>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={state.name} onChange={(event) => {setState({name: event.target.value})}}/>
            <label htmlFor="email">Email:</label>
            <input type="text" id="email" name="email" value={state.email} onChange={(event) => {setState({email: event.target.value})}}/>
            <label htmlFor="comment">Comment:</label>
            <input type="text" id="comment" name="comment" value={state.comment} onChange={(event) => {setState({comment: event.target.value})}}/>
            <button type="submit">submit</button>
        </Form>
    );
}
