import CommentItem from './CommentItem'

export function seperateChildren(elements){
  var childrenElements = []

  if (!elements) {
    return childrenElements
  }
  elements.forEach(el => {
    if (
      childrenElements[el.parent] !== null &&
      typeof childrenElements[el.parent] == "object"
    ) {
      childrenElements[el.parent].push(el)
    } else {
      childrenElements[el.parent] = [el]
    }
  })
  return childrenElements
}

export function buildTree(children, parent){
  if (children[parent]==null){
    return
  }
  return (
    <div>
      {children[parent].map(child => (
          <CommentItem key={child.id} comment={child}>
            {buildTree(children, child.id)}
          </CommentItem>
      ))}
    </div>
  )
}
