        listItems: [],
        isLoaded: false,
        menu: false
    }

    componentDidMount(){
        axios('/wp-json/menus/v1/menus/main').then(res => {
            const Items = res.data.items.map((item) =>
                <NavLink key={item.title} href={item.url}>{item.title}</NavLink>
            );
            this.setState({
                listItems : Items,
            })
        });
    }

