import React, { useState, useEffect} from 'react';
import {retrieveNavbar} from '../../service/WPService';
import styled, {css} from 'styled-components'
import ReorderIcon from '@material-ui/icons/Reorder'
import WbSunnyIcon from '@material-ui/icons/WbSunny';
import Brightness2Icon from '@material-ui/icons/Brightness2';

const TopNav = styled.div`
    position:fixed;
    top:0;
    transition: all .5s ease-in-out;
    z-index:999;
    height:var(--navbar-height);
    width:100%;
    display:flex;
    justify-content:center;
    background-color: var(--p-dark);
    border-bottom: 1px var(--p-vlight) solid;
    @media (max-width: 48em) {
        flex-direction:column;
        height:auto;
    }
    ${props => props.background && css`
        background-color:transparent;
        border-bottom: none;
    `}
`
const NavItem = styled.li`
    list-style-type: none;
    display:flex;
    align-items:center;
    justify-content:center;
    transition: all 0.3s ease;
    margin:.2em;
    @media (max-width: 48em) {
        width:auto;
    }
`

const AnchorButton = styled.a`
    min-width:100px;
    text-align: center;
    text-decoration: none;
    width:auto;
    padding:.5em;
    margin:0 .5em 0 0;
    color: var(--p-vlight);
    border: 1px var(--p-vlight) solid;
    :hover{
        color: var(--s-5);
        transition:.5s;
        -webkit-transition:.5s;
        border: 1px var(--s-5) solid;
    }
    @media (max-width: 48em) {
        width:100%;
    }
    ${props => props.background && css`
        color: var(--p-vdark);
    `}
`
const Logo = styled(AnchorButton)`
    margin-right: auto;
    border: 1px transparent solid;
    @media (max-width: 48em) {
        width:8em;
    }

`

const Right = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-end;
width:50%;
max-width:32em;
@media (max-width: 48em) {
    background-color:var(--p-dark);
    flex-direction:column;
    margin-left:0;
    margin-bot:5em;
    border-bottom: 2px var(--p-vlight) solid;
    width:100%;
    max-width:100%;
    ${props => !props.show && css`
        display:none;
    `}
}
`
const Left = styled.div`
    width:50%;
    max-width:32em;
    display:flex;
    justify-content:space-between;
    @media (max-width: 48em) {
        width:100%;
        max-width:100%;
    }
`

const Burger = styled.a`
    color:var(--p-vlight);
    display:none;
    text-align: center;
    transition: all 0.3s ease;
    text-decoration: none;
    padding:.8em;
    @media (max-width: 48em) {
        display:inline;
    }
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
    }
    ${props => props.background && css`
        color: var(--p-vdark);
    `}
`

const Button = styled.button`
    border: none;
    min-width:100px;
    text-align: center;
    text-decoration: none;
    width:auto;
    border-radius: .5em;
    padding:.8em;
    color: var(--p-vlight);
    background-color: transparent;
    :hover{
        color: var(--s-5);
        transition:1.5s;
        -webkit-transition:1.5s;
        border-radius:.3em;
    }
    @media (max-width: 48em) {
        width:100%;
    }
`

const Menu = styled.div`
    dispaly:none;
    position:relative;
    top:0;
    left:0;
    height:100vh;
    width:100vw;
    ${props => props.show && css`
        display:block;
    `}
`

export default function Navbar (props) {
    const [listItems, setListItems] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [clicked, setClicked] = useState(false);
    const [themeChanger, setThemeChanger] = useState(<Brightness2Icon/>);
    const [background, setBackground] = useState(true);

    const changeBackground = () =>{
        if(window.scrollY >= 60){
            setBackground(false);
        } else {
            setBackground(true);  
        }
    }

    useEffect( () => {

        window.addEventListener("scroll", changeBackground);
        const current = localStorage.getItem('theme');
        if(current==='dark'){
            setThemeChanger(<WbSunnyIcon/>)
        } else if(current==='light'){
            setThemeChanger(<Brightness2Icon/>)
        }

        retrieveNavbar()
        .then(res => {
            if(res.items.type === 'undefined'){

            } else {
                const Items = res.items.map((item) =>
                <NavItem key={item.title}>
                    <AnchorButton key={item.title} href={item.url}>{item.title}</AnchorButton>
                </NavItem>
                );
                setListItems(Items)
                setLoaded(true);
            }
        });
        return () => {
            setLoaded(false);
            window.removeEventListener("scroll", changeBackground);
        }
    },[])

    const showLinks = () => {
        let show = clicked;
        setClicked(!show)
    }

    const changeTheme = () => {
        if(props.theme==='dark'){
          props.setTheme('light')
          localStorage.setItem('theme', 'light');
          setThemeChanger(<Brightness2Icon/>)
        } else {
          props.setTheme('dark')
          localStorage.setItem('theme', 'dark');
          setThemeChanger(<WbSunnyIcon/>)
        }
    }

    if(loaded){
        return (
                <TopNav background={background}>
                    <Left>
                        <NavItem>
                            <Logo background={background} mr href='/'>Milan Tornier</Logo>
                        </NavItem>
                        <NavItem>
                            <Burger background={background} onClick={showLinks}><ReorderIcon/></Burger>
                        </NavItem>
                    </Left>
                    <Right>
                        {listItems}
                        <Button onClick={changeTheme}> {themeChanger} </Button>
                    </Right>
                    <Menu show={clicked}>
                        {listItems}
                        <Button onClick={changeTheme}> {themeChanger} </Button>
                    </Menu>
                </TopNav>
        )
    }
    else {
        return null;
    }
}
