import React, {useState} from 'react'

import {Wrapper} from '../Styled';
import styled, {keyframes, css} from "styled-components";
import School from '@material-ui/icons/School';
import Construction from '@material-ui/icons/Build';
import Camera from '@material-ui/icons/CameraAlt';

const Container = styled(Wrapper)`
    min-height:70vh;
    justify-content:flex-start;
    align-items:center;
`

const Item = styled.div`
    width:100%;
    max-width:30em;
    margin:.2em;
`

const TopBar = styled.div`
    height:48px;

    background-color:var(--p-0);
    display:flex;
    align-items:center;

    border-radius: 24px 24px 0 24px;
    margin-bottom:1em;
`

const Title = styled.h5`
    color: var(--p-10);
    margin:0;
    padding-left:8px;
`

const Circle = styled.div`
    height:40px;
    width:40px;
    margin:4px;
    background-color:var(--s-3);
    border-radius:50%;
    display:flex;
    justify-content:center;
    align-items:center;
`

const Info = styled.div`
    max-width:100%;
    color: white;
    min-height:100px;
    margin: 0 0 0 24px;
    transform-origin:top;
    ${props => props.display && css`
        animation:${dot} 1s linear forwards;
    `}
    ${props => !props.display && css`
        display:none;
    `}
`
const Column = styled.div`
    max-width:100%;
    min-height:32px;
    padding:8px;
    display:flex;
    flex-direction:column;
    align-items:flex-start;
    justify-content:flex-start;
    position:relative;
    padding-bottom:32px;
`

const dot = keyframes`
    0% {
        opacity:0;
    }
    100% {
        opacity:1;
    }
`;

const ColumnTitle = styled.h6`
    opacity:0;
    color: var(--p-0);
    font-size:1em;
    position:absolute;
    top:-.5em;
    margin-left:8px;
    animation:${dot} .4s ${props => props.delay} ease-in-out forwards;
`

const Text = styled.p`
    opacity:0;
    color: var(--p-0);
    margin:0;
    padding:0;
    margin-left:16px;
    margin-top:12px; 
    animation:${dot} 1s 2s ease-in-out forwards;
`
const Time = styled.span`
    opacity:0;
    color: var(--p-3);
    font-size:12px;
    margin:0;
    padding:0;
    margin-left:8px;
    margin-top:6px; 
    animation:${dot} .4s ${props => props.delay} ease-in-out forwards;
`



const Dot = styled.div`
    opacity:0;
    width:16px;
    height:16px;
    border-radius:8px;
    background-color:var(--s-3);
    position:absolute;
    top:-8px;
    left:-8px;
    animation:${dot} .4s ${props => props.delay} ease-in-out forwards;
`

const line = keyframes`
    0% {
        transform:scale(0);
    }
    100% {
        transform:scale(1);
    }
`;

const Line = styled.div`
    width:2px;
    height:100%;
    background-color:var(--s-3);
    position:absolute;
    left:-1px;
    top:0px;
    transform-origin:top;
    transform:scale(0);
    animation:${line} .4s ${props => props.delay} ease-in-out forwards;
`

export default function Lebenslauf() {
    const [active, setActive] = useState(0);

    const handleClick = (number) => {
        if(number === active){
            setActive(0)
        } else {
            setActive(number)
        }
    }

    return (
        <Container>
            <Item>
                <TopBar onClick={()=>handleClick(1)}>
                    <Circle>
                        <School/>
                    </Circle>
                    <Title>Education</Title>
                </TopBar>

                <Info display={active===1}>
                    <Column>
                        <Dot delay={"0s"}/>
                        <Line delay={".4s"}/>
                        <ColumnTitle delay={"0s"}>Dathe</ColumnTitle>
                        <Time delay={"0s"}>2018-2019</Time>
                        <Text>Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</Text>
                    </Column>
                    <Column>
                        <Dot delay={".8s"}/>
                        <Line delay={"1.2s"}/>
                        <ColumnTitle delay={".8s"}>Dathe</ColumnTitle>
                        <Time delay={".8s"}>2018-2019</Time>
                        <Text>Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</Text>
                    </Column>
                    <Column>
                        <Dot delay={"1.6s"}/>
                        <Line delay={"2s"}/>
                        <ColumnTitle delay={"1.6s"}>Dathe</ColumnTitle>
                        <Time delay={"1.6s"}>2018-2019</Time>
                        <Text>Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</Text>
                    </Column>
                </Info>

            </Item>
            <Item>
                <TopBar onClick={()=>handleClick(2)}>
                    <Circle>
                        <Construction/>
                    </Circle>
                    <Title>Skills</Title>
                </TopBar>
                {active===2 &&
                    <p>expand</p>
                }
            </Item>
            <Item>
                <TopBar onClick={()=>handleClick(3)}>
                    <Circle>
                        <Camera/>
                    </Circle>
                    <Title>Interests</Title>
                </TopBar>
                {active===3 &&
                    <p>expand</p>
                }
            </Item>
        </Container>
    )
}
