import React, {Component} from 'react';
import {Wrapper, Loading} from './Styled'
import styled from 'styled-components'
import Comments from './Comments/Comments'
import { retrieveFeaturedMedia, retrievePost } from '../service/WPService';

const Article = styled.article`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const Image = styled.img`
    width:1000px;
    height:35em;
    object-fit: cover;
    display:block;
`

const Content = styled.div`
    background-color: white;
    transform: translate(0em, -5em);
    margin: 0rem 2rem;
    padding:2rem;

`
export class PostPage extends Component {

    state =Â {
        post: '',
        imageUrl: '',
        isLoaded: false,
        depth:0
    }

    componentDidMount(){        
        const { id } = this.props.match.params;
        retrievePost(id)
        .then( res =>{
            this.setState({
                post: res,
            })
            return retrieveFeaturedMedia(res.featured_media)
        })
        .then(res => {
            this.setState({
                imageUrl : res.media_details.sizes.full.source_url,
                isLoaded: true
            })
        })

    }

    render(){ 
        const {depth, post, isLoaded, imageUrl } = this.state;
        if(isLoaded){
            return(
            <Wrapper>
                <Article>
                    <Image src={imageUrl}  alt="img"/>
                    <Content dangerouslySetInnerHTML={{__html: post.content.rendered}}/>
                </Article>
                <Comments parent="0" post={post.id} depth={depth}/>
            </Wrapper>
            )
        } else {
            return (
                <Loading>Loading...</Loading>
            )
        }
    }
  }

export default PostPage
