import React, {useState, useEffect} from 'react';
import {useParams} from "react-router-dom";
import axios from 'axios';


function PostPage(props) {
    const [post, setPost] = useState('');
    const [img, setImg] = useState('');
    const [content, setContent] = useState('');

    const params = useParams();

    console.log(params.id);

    useEffect( () => {
        axios(`/wp-json/wp/v2/posts/${params.id}`)
        .then(res => setPost(res.data))
        .catch(err => console.error(err))

        const getImageUrl = axios(`/wp-json/wp/v2/media/${post.featured_media}`);
        const getContent = axios(`/wp-json/wp/v2/users/${post.content}`);
        Promise.all([getImageUrl, getContent]).then(res => {
            this.setState({
                imageUrl: res[0].data.media_details.sizes.full.source_url,
                content: res[1].data.name,
                isLoaded: true
            })
        });

    }, [params.id]);

    console.log(post);

    return (
        <div className="post-page">
            <div className="image">
                <img  alt="warum"/>
            </div>
            <div className="post">
            </div>
            <p>comments</p>
        </div>
    );
  }

export default PostPage
