import React, {Component} from 'react';
import axios from 'axios';
import {Wrapper, Loading} from './Styled'
import styled from 'styled-components'
import Comments from './Comments'

const Article = styled.article`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const Image = styled.img`
    width:1000px;
    height:35em;
    object-fit: cover;
    display:block;
`

const Content = styled.div`
    background-color: white;
    transform: translate(0em, -5em);
    margin: 0rem 2rem;
    padding:2rem;

`

export class PostPage extends Component {

    state =Â {
        post: '',
        comments: [],
        imageUrl: '',
        isLoaded: false
    }

    componentDidMount(){        
        const { id } = this.props.match.params;

        axios(`/wp-json/wp/v2/posts/${id}`)
        .then(res => {
            this.setState({
                post: res.data,
            })
            return axios(`/wp-json/wp/v2/media/${res.data.featured_media}`)
        })
        .then(res => {
            this.setState({
                imageUrl : res.data.media_details.sizes.full.source_url,
                isLoaded: true
            })
        })
        .catch(err => console.error(err))

        axios({
            method: 'get',
            url: `/wp-json/wp/v2/comments`,
            headers: {}, 
            data: {
              post: `${id}`,
            }
        })
        .then(res => {
            this.setState({
                comments: res.data,
            })
        })
        .catch(err => console.error(err))
    }

    render(){ 
        const { post, isLoaded, imageUrl, comments } = this.state;
        if(isLoaded){
            return(
            <Wrapper>
                <Article>
                    <Image src={imageUrl}  alt="warum"/>
                    <Content dangerouslySetInnerHTML={{__html: post.content.rendered}}/>
                    <Comments id='0' comments={comments.slice(0).reverse()}/>
                </Article>
            </Wrapper>
            )
        } else {
            return (
                <Loading>Loading...</Loading>
            )
        }
    }
  }

export default PostPage
