    useEffect(() => {

        axios({
            method: 'get',
            url: `/wp-json/wp/v2/comments/?post=${id}&per_page=10`,
            headers: {'Content-Type': 'application/json'}
        })
        .then(res => {
            setComments(res.data)
        })
        .catch(err => console.error(err))

    }, [id])


