import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import {Link} from "react-router-dom";
import styled from "styled-components";

const Info = styled.div`
    width: 25em;
    height: 25em;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    border:2px solid var(--primary);
    transform: translate(0em, 2.5em);
    background-color:white;
    padding:0em 1em 1em 1em;
    @media (max-width: 45em) {
        width:20em;
        transform: translate(0em, 0em);
        background: url(${props => props.img}) no-repeat center center fixed; 
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
        background-size: cover;
    }
`
const ImageContainer = styled.div`
    margin-left:auto;
    margin-right:0;
    transform: translate(2.5em, -5em);
    @media (max-width: 45em) {
        display:none;
    }
`
const Image = styled.img`
    width:20em;
    height:25em;
    object-fit: cover;
    display:block;
    @media (max-width: 45em) {
    }
`
const Item = styled.div`
    background: linear-gradient(90deg, var(--primary) 10em, var(--primary50) 10em);
    max-width: 800px;
    display: flex;
    margin: 10em 0em 0em 0em;
    margin-right:auto;
    @media (max-width: 45em) {
       flex-direction:column; 
       margin:0;
       background:none;
       margin:2.5em 0;
    }
    :nth-child(even){
        background: linear-gradient(to left, var(--primary) 10em, var(--primary50) 10em);
        margin: 0em 0em 20em 0em;
        margin-left:auto;
        @media (max-width: 45em) {
            background-image: url(${props => props.img});
            margin:0;
            margin:2.5em 0;
         }
    }
    :nth-child(even) ${ImageContainer}{
        transform: translate(-2.5em, 5em);
        order:2;
        margin-left:0;
        margin-right:auto;
    }
    :nth-child(even) ${Info}{
        transform: translate(0em, 12.5em);
        @media (max-width: 45em) {
            transform: translate(0em, 0em);
         }
    }

`
const Titel = styled.h1`
    text-align: center;
    margin: 0em;
    padding: 0em;
    @media (max-width: 45em) {
        background-color:white;
        padding:.5em;
        border-radius:.5em;
        border: 2px var(--s-dark) solid;
        margin:0;
        margin-top:5em;
     }
`
const Excerpt = styled.p`
    overflow: hidden;
    margin: 0em;
    padding: 0em;
    @media (max-width: 45em) {
        display:none;
    }
`

const ReadMore = styled(Link)`
    background-color: var(--secondary600);
    color:white;
    border-radius: .4em;
    display: inline-block;
    text-align: center;
    text-decoration: none;
    border:none;
    width: 10em;
    margin:1em 0em 0em 0em;
    padding:.5em;
    :hover{
        background-color: Black;
        color:var(--secondary600);
        transition: .5s;
    }
`

export class PostItem extends Component {

    state =Â {
        imageUrl: '',
        author: '',
        isLoaded: false
    }

    static propTypes = {
        post: PropTypes.object.isRequired,
    }

    componentDidMount(){
        const { featured_media, author } = this.props.post;
        const getImageUrl = axios(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios(`/wp-json/wp/v2/users/${author}`);
        Promise.all([getImageUrl, getAuthor]).then(res => {
            this.setState({
                imageUrl: res[0].data.media_details.sizes.full.source_url,
                author: res[1].data.name,
                isLoaded: true
            })
        });
    }

    render() {
        const { title, excerpt, id}  = this.props.post;
        const { imageUrl, isLoaded} = this.state;
        if(isLoaded){
            return (
                <Item>
                    <ImageContainer>
                        <Image src={imageUrl} alt={title.rendered}/>
                    </ImageContainer>
                    <Info img={imageUrl}>
                        <Titel>
                            {title.rendered}
                        </Titel>
                        <Excerpt dangerouslySetInnerHTML={{__html: excerpt.rendered.substring(0,300)+'...' }}/>
                        <ReadMore to={{
                            pathname: `/post/${id}`
                        }}>
                            Read More.
                        </ReadMore>
                    </Info>
                </Item>
            )
        }
        else {
            return (null);
        }
    }
}

export default PostItem
