import React, {useState, useEffect} from 'react'
import  {useInView} from 'react-intersection-observer'

import styled, {css} from 'styled-components'
import { retrieveFeaturedMedia, retrievePageByName } from '../service/WPService';
import {Spinner, Loading, Wrapper, H1} from '../components/Styled';
import Instagram from '@material-ui/icons/Instagram';
import LinkedIn from '@material-ui/icons/LinkedIn';
import { animate } from 'dom-helpers';

const StyledWrapper = styled(Wrapper)`
    background: linear-gradient(0deg ,var(--p-10) 0% ,var(--p-10) 50% ,var(--p-10) 100% );
    padding-top:0;
    margin:0;
    min-height:100vh;
    @media (min-width: 32em) {
        padding:0 var(--margin);
        flex-direction:column;
        justify-content:center;
        display:flex;
    }
`

const Upper = styled.div`
    display:flex;
    flex-direction:column;
    justify-content:space-around;
    align-items:center;
    height:auto;
    margin-bottom:var(--margin);
    @media (min-width: 32em) {
        flex-direction:row;
    }
`

const Info = styled.div`
    display:flex;  
    flex-direction:column;
    justify-content:space-around;
    height:auto;
    padding:var(--margin);
    order:3;
    @media (min-width: 32em) {
        width:50%;
        margin-right:var(--margin);
        padding:0;
        order:0;
    }
    transition: 1s ease-in-out;
    opacity:0;
    ${props => props.appear && css`
        opacity:1;
    `}
`
const Social = styled.div`

    @media (min-width: 32em) {
        margin:calc(var(--margin)) 0;
        order:3;
    }
`

const SocialList = styled.ul`
    display:flex;
    justify-content:flex-end;
    margin:0;
    padding:0;
    width:100%;
    justify-content:flex-start;
    @media (min-width: 32em) {
        align-items:flex-start;
        justify-content:flex-start;

    }
`

const SocialItem = styled.li`
    list-style-type: none;
    display:flex;
    align-items:center;
    justify-content:center;
    padding:calc(var(--margin)/2) calc(2*var(--margin)) calc(var(--margin)/2) 0;
    @media (min-width: 32em) {
        padding:calc(var(--margin)/2) 0 0 0;
        margin:0 2em 0 0;
    }
`

const SocialLink = styled.a`
    text-decoration: none;
    color: var(--s-3);
    :hover{
        transition:.5s;
        -webkit-transition:.5s;
        color: var(--p-3);
    }
`

const Text = styled.div`
    margin:1em;
    p{
        color:var(--p-1);
        margin:0;
    }
    text-align:justify;
    @media (min-width: 32em) {
        width:100%;
        margin:0;
        padding:1em;
        order:2;
    }
`

const Image= styled.img`
    max-height:400px;
    max-width:400px;
    width:100%;
    transition: 1s ease-in-out;
    transform: translateY(-200px);
    opacity:0;
    ${props => props.appear && css`
        opacity:1;
        transform: translateX(0px);
    `}
    @media (min-width: 32em) {
        width:50%;
        transform: translateX(200px);
    }
`
export default function Welcome({innerRef, appear}) {
    const options1= {
        "threshold": .5,
        "rootMargin": "0px 0px"
    }

    const {ref: ref1, inView: visible1} = useInView(options1);
    const [animate1, setAnimate1] = useState();

    const [welcome, setWelcome] = useState()
    const [isLoaded, setIsLoaded] = useState(false)

    const [imageUrl, setImageUrl] = useState('');

    const setStuff = async () => {
        const page = await retrievePageByName("Welcome");
        setWelcome(page)
        if(page===null){
            return;
        }
        retrieveFeaturedMedia(page.featured_media)
        .then(res => {
            setImageUrl(res.media_details.sizes.full.source_url)
            setIsLoaded(true)
        })
    }

    useEffect( () => {
        if(!animate1){
            setStuff()
        }
        if(visible1){
            setAnimate1(true);
        }
        return function cleanup() {
            setIsLoaded(false);
        }
    },[visible1])

    if(isLoaded){
        return (
            <StyledWrapper ref={ref1} appear={animate1}>
                <Upper>
                    <Info appear={animate1}>
                        <H1 dangerouslySetInnerHTML={{__html: welcome.title.rendered}}/>
                        <Text  dangerouslySetInnerHTML={{__html: welcome.content.rendered}}/>
                        <Social>
                            <SocialList>
                                <SocialItem>
                                    <SocialLink href="https://www.instagram.com/milan.tornier/"><Instagram/></SocialLink>
                                </SocialItem> 
                                <SocialItem>
                                    <SocialLink href="https://www.linkedin.com/in/milan-tornier-ab74bb136/"><LinkedIn/></SocialLink>
                                </SocialItem>
                            </SocialList>
                        </Social>
                    </Info>
                    <Image src={imageUrl} appear={appear}/>
                </Upper>
            </StyledWrapper>
        )
    }
    else{
        return (
            <Loading id="about">
                <Spinner/>
            </Loading>
        )
    }
}
