import React, { useState, useEffect} from 'react';
import {retrieveNavbar} from '../../service/WPService';
import styled, {css, keyframes} from 'styled-components'
import ReorderIcon from '@material-ui/icons/Reorder'
import WbSunnyIcon from '@material-ui/icons/WbSunny';
import Brightness2Icon from '@material-ui/icons/Brightness2';
import Close from '@material-ui/icons/Close';

const changeBackground = keyframes`
    from {
        background-color: var(--p-9);
        border-bottom: 1px var(--p-1) solid;
    }
    to {
        background-color:transparent;
        border-bottom: none;
    }
`;
const changeBackgroundBack = keyframes`
    from {
        background-color:transparent;
        border-bottom: none;
    }
    to {
        background-color: var(--p-9);
        border-bottom: 1px var(--p-1) solid;
    }
`;

const NavContainer = styled.div`
    position:fixed;
    top:0;
    z-index:500;
    width:100%;
    background-color: transparent;
    -webkit-transition: all 0.5s ease-in-out;
    ${props => props.transparent && css`
        animation: ${changeBackground} 1s forwards;
    `}
    ${props => props.notTransparent && css`
        animation: ${changeBackgroundBack} 1s forwards;
    `}
`

const TopNav = styled.div`
    height:var(--navbar-height);
    width:100%;
    max-width:80em;
    margin:0 auto;
    display:flex;
    justify-content:center;
`

const Links = styled.div`
    display:none;
    @media (min-width: 48em) {
        display:flex;
        justify-content:center;
    }
`

const AnchorButton = styled.a`
    text-align: center;
    margin:.5em;
    text-decoration: none;
    width:auto;
    padding:.5em;
    color: var(--p-1);
    border: 1px var(--p-1) solid;
    :hover{
        color: var(--s-5);
        transition:.5s;
        -webkit-transition:.5s;
        border: 1px var(--s-5) solid;
    }
    @media (min-width: 48em) {
        width:8em;
    }
    ${props => props.transparent && css`
        color: var(--p-1);
        border: 1px var(--p-1) solid;
        @media (min-width: 32em) {
            color: var(--p-1);
        }
    `}
`
const LogoButton = styled(AnchorButton)`
    margin-right: auto;
    border: 1px transparent solid;
    width:8em;
    @media (min-width: 48em) {
    }

`

const Burger = styled(AnchorButton)`
    border:none;
    @media (min-width: 48em) {
        display:none;
    }
    :hover{ 
        border:none;
    }
`

const ThemeChanger = styled(AnchorButton)`
    border:none;
    :hover{ 
        border:none;
    }
`

const Cancel = styled.a`
    position:absolute;
    top:12px;
    right:16px;
    color: var(--p-1);
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
    }
`

const showMenu = keyframes`
    from {
        right:-100vw;
    }
    to {
        right:0vw;
    }
`;

const dontShowMenu = keyframes`
    to {
        right:-100vw;
    }
    from {
        right:0vw;
    }
`;

const Menu = styled.div`
    z-index:750;
    position:fixed;
    top:0;
    height:100vh;
    width:100%;
    right:-100vw;
    padding:var(--margin);
    background-color:var(--p-9);   
    transition: all 0.5s ease-in-out;
    -webkit-transition: all 0.5s ease-in-out;
    display:flex;
    flex-direction:column;
    justify-content:center;
    ${props => props.show && css`
        transform: translateX(100vw);
    `}
    ${props => props.dontShow && css`
        animation: ${dontShowMenu} .3s forwards ease-in-out;
    `}
    @media (min-width: 48em) {
        display:none;
    }
`

export default function Navbar (props) {
    const [listItems, setListItems] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [showMenu, setShowMenu] = useState(false);
    const [dontShowMenu, setdontShowMenu] = useState(false);

    const [themeChanger, setThemeChanger] = useState(<Brightness2Icon/>);
    const [transparent, setTransparent] = useState(false);
    const [notTransparent, setNotTransparent] = useState(false);

    const changeTransparent = () =>{
        if(window.scrollY >= 28){
            setTransparent(false);
            setNotTransparent(true);
        } else {
            setTransparent(true);  
            setNotTransparent(false);
        }
    }

    const displayMenu = () => {
        setShowMenu(true)
        setdontShowMenu(false)
    }

    const hideMenu = () => {
        setShowMenu(false)
        setdontShowMenu(true)
    }

    useEffect( () => {

        window.addEventListener("scroll", changeTransparent);
        const current = localStorage.getItem('theme');
        if(current==='dark'){
            setThemeChanger(<WbSunnyIcon/>)
        } else if(current==='light'){
            setThemeChanger(<Brightness2Icon/>)
        }

        retrieveNavbar()
        .then(res => {
            if(res.items.type === 'undefined'){

            } else {
                const Items = res.items.map((item) =>
                    <AnchorButton transparent={transparent} key={item.title} href={item.url}>{item.title}</AnchorButton>
                );
                setListItems(Items)
                setLoaded(true);
            }
        });
        return () => {
            setLoaded(false);
            window.removeEventListener("scroll", changeTransparent);
        }
    },[transparent])

    const changeTheme = () => {
        if(props.theme==='dark'){
            props.setTheme('light')
            localStorage.setItem('theme', 'light');
            setThemeChanger(<Brightness2Icon/>)
        } else {
            props.setTheme('dark')
            localStorage.setItem('theme', 'dark');
            setThemeChanger(<WbSunnyIcon/>)
        }
        setTransparent(true);
    }

    if(loaded){
        return (
            <NavContainer transparent={transparent} notTransparent={notTransparent}>
                <TopNav>
                    <LogoButton transparent={transparent} mr href='/'>Milan Tornier</LogoButton>
                    <Burger transparent={transparent} onClick={displayMenu}><ReorderIcon/></Burger>
                    <Links>
                        {listItems}
                        <ThemeChanger transparent={transparent} onClick={changeTheme}> {themeChanger} </ThemeChanger>
                    </Links>
                    <Menu show={showMenu} dontShow={dontShowMenu}>
                        {listItems}
                        <Cancel onClick={hideMenu}><Close/></Cancel>
                        <ThemeChanger transparent={transparent} onClick={changeTheme}> {themeChanger} </ThemeChanger>
                    </Menu>
                </TopNav>
            </NavContainer>
        )
    }
    else {
        return null;
    }
}
