import {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios';


const Form = styled.form`
display:flex;
flex-direction:column;
`
const Label = styled.label``

const Input = styled.input`
width=100%;
`


export default function CommentForm(props) {
    const [parent] = useState(props.parent)
    const [post] = useState(props.post)

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [comment, setComment] = useState('')

    const handleSubmit = (event) =>{

        event.preventDefault();
        const data = JSON.stringify({
            post: post,
            author_name: name,
            author_email: email,
            content:comment,
            parent:parent
          });

          console.log(data);
        axios({
            method: 'post',
            url: `/wp-json/wp/v2/comments`,
            headers: {'Content-Type': 'application/json'}, 
            data: data
        })
        .then(res => console.log(res))
        .catch(err => console.error(err))
      }

        return (
            <Form onSubmit={handleSubmit.bind(this)}>
                <div>
                    <Label htmlFor="name"></Label>
                    <Input default='name'type="text" id="name" name="name" value={name} onChange={(event) => {setName(event.target.value)}}/>
                    <Label htmlFor="email"></Label>
                    <Input type="text" id="email" name="email" value={email} onChange={(event) => {setEmail(event.target.value)}}/>
                </div>
                <div>
                    <Label htmlFor="comment"></Label>
                    <Input type="text" id="comment" name="comment" value={comment} onChange={(event) => {setComment(event.target.value)}}/>
                </div>
                <button type="submit">submit</button>
            </Form>
        );
    }
