import React, {useState} from 'react'
import {Wrapper, H1} from '../Styled';
import Input from './Input'

import styled from "styled-components";

const Container = styled(Wrapper)`
    min-height:80vh;
`

const Form = styled.form`
    border: 2px solid var(--s-3);
    border-radius:.5em;
    padding:1em;
    width:40em;
    margin:0 auto;
    display:flex;
    flex-direction:column;
    align-items:center;
`

const line = keyframes`
    0% {
        transform:scale(0);
    }
    100% {
        transform:scale(1);
    }
`;

const ValidationHints = styled.div`
    width:290px;
    p{
        margin:0 1em;
        background-color:var(--s-3);
        color:var(--p-10);
        border-radius:0 0 .3em .3em;
        padding:8px 16px;
    }
    transform-origin:top;
    transform:scale(0);
    animation:${line} .4s ease-in-out forwards;
`

export default function Contact() {
    const [name, setName] = useState('')
    const [eMail, setEMail] = useState('')
    const [nameError, setNameError] = useState('')

    const nameChange = (name) =>  {
        console.log(name.length)
        console.log(name)
        if(name.length < 3){
            setNameError("Name must have 3 letters")
        } else {
            setNameError(null)
        }
    }

    return (
        <Container>
            <H1>Contact</H1>
            <Form>
                <Input label='Name' onchange={(name) => nameChange(name)} value={name} setValue={(name) => setName(name)} />
                {nameError ? <ValidationHints><p>{nameError}</p></ValidationHints>: null}
                <Input label='E-Mail' value={eMail} setValue={(name) => setEMail(name)} />
            </Form>
        </Container>
    )
}
