import {Component} from 'react'
import styled from 'styled-components'
import axios from 'axios';


const Form = styled.form`
display:flex;
flex-direction:column;
`

export class CommentForm extends Component {

    state =Â {
        post: this.props.post.id,
        name:'',
        email:'',
        content:'',
    }

    handleSubmit(event, state){
        event.preventDefault();
        const { post, name, email, content } = this.state;
        const data = JSON.stringify({
            post: post,
            author_name: name,
            author_email: email,
            content: content,
          });
        axios({
            method: 'post',
            url: `/wp-json/wp/v2/comments`,
            headers: {}, 
            data: data
        })
        .catch(err => console.error(err))
      }
    render(){
        const { name, email, content } = this.state;

        return (
            <Form onSubmit={this.handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" value={name} onChange={(event) => {this.setState({name: event.target.value})}}/>
                <label htmlFor="email">Email:</label>
                <input type="text" id="email" name="email" value={email} onChange={(event) => {this.setState({email: event.target.value})}}/>
                <label htmlFor="comment">Comment:</label>
                <input type="text" id="comment" name="comment" value={content} onChange={(event) => {this.setState({comment: event.target.value})}}/>
                <button type="submit">submit</button>
            </Form>
        );
    }
}
export default CommentForm
