import {useState, useEffect} from 'react'
import CommentItem from './CommentItem'
import CommentForm from './CommentForm'
import {retrieveComments} from '../../service/WPService'

const Buttons = styled.div`
    display:flex;
    justify-content:space-between;
    p{
        margin:0;
    }
`

const Button = styled.button`
    border: none;
    background-color: transparent;
    color: var(--secondary)
`

export default function Comments(props) {

    const [comments, setComments] = useState([])
    const [loaded, setLoaded] = useState(false)
    const [id]  = useState(props.post)
    const [parent] = useState(props.parent)
    const [updated, setUpdated] = useState(false);

    const [hideForm, setHideForm] = useState(props.hideForm);

    const [reply] = useState(props.reply)

    useEffect( () => {

        retrieveComments(id, parent).then( res =>
            setComments(res),
            setLoaded(true),
            setUpdated(false)
        )

    }, [id, parent, updated])

    const showReplies = () => {
        setHideReplies(!hideReplies);
    }

    const showForm = () => {
        setHideForm(!hideForm);
    }

        if(loaded){
            return (
                <div>
                    <Buttons>
                        {!reply && <Button onClick={() => showReplies()}>Show replies</Button> }
                        {!reply && <Button onClick={() => showForm()}>reply</Button>}
                    </Buttons>
                    {hideform && 
                        <CommentForm
                            updated={a => setUpdated(a)} 
                            post={id}
                            key={'commentForm'} 
                        />
                    }
                    {comments.map(comment => (
                        <CommentItem 
                            reply={reply}
                            key={comment.id} 
                            comment={comment}
                        />
                    ))}
                </div>
            )
        } else {
            return 'loading...'
        }
}
