import React, {useState} from 'react'
import styled from 'styled-components'
import CommentForm from './CommentForm'
import Replies from './CommentForm'

const Comment = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:1em;
    margin:1em;
    display:flex;
    max-width:55em;
    justify-content:space-between;
`

export default function CommentItem (props) {
    const [hideReplies, setHideReplies] = useState(false);
    const [hideForm, setHideForm] = useState(false);

    const showReplies = () => {
        setHideReplies(!hideReplies);
        console.log('hideReplies')

    }

    const showForm = () => {
        setHideForm(!hideForm);
        console.log('hideForm')
    }

    return (
        <div>
            <Comment dangerouslySetInnerHTML={{__html: props.comment.content.rendered}}>
            </Comment>
            <button onClick={() => showReplies()}>Show replies</button>
            <button onClick={() => showForm()}>reply</button>
            {!hideReplies && <CommentForm key={'f'+props.comment.id} post={props.comment.post}></CommentForm>}
            {!hideForm && <Replies key={'r'+props.comment.id} post={props.comment.post} comment={props.comment.id}></Replies>}
        </div>
    )
}
