import React, { useState, useEffect} from 'react';
import {retrieveNavbar} from '../../service/WPService';
import styled, {css} from 'styled-components'
import ReorderIcon from '@material-ui/icons/Reorder'
import WbSunnyIcon from '@material-ui/icons/WbSunny';
import Brightness2Icon from '@material-ui/icons/Brightness2';
import Close from '@material-ui/icons/Close';


const TopNav = styled.div`
    position:fixed;
    top:0;
    transition: all .5s ease-in-out;
    z-index:500;
    height:var(--navbar-height);
    width:100%;
    display:flex;
    justify-content:center;
    background-color: var(--p-dark);
    border-bottom: 1px var(--p-vlight) solid;
    @media (min-width: 48em) {
    }
    ${props => props.transparent && css`
        background-color:transparent;
        border-bottom: none;
    `}
`

const AnchorButton = styled.a`
    text-align: center;
    margin:0;
    text-decoration: none;
    width:auto;
    padding:.5em;
    color: var(--p-vlight);
    border: 1px var(--p-vlight) solid;
    :hover{
        color: var(--s-5);
        transition:.5s;
        -webkit-transition:.5s;
        border: 1px var(--s-5) solid;
    }
    @media (min-width: 48em) {
        width:100%;
    }
    ${props => props.transparent && css`
        color: var(--p-vdark);
        border: 1px var(--p-vdark) solid;
    `}
`
const LogoButton = styled(AnchorButton)`
    margin-right: auto;
    border: 1px transparent solid;
    @media (max-width: 48em) {
        width:8em;
    }

`

const Right = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-end;
width:50%;
max-width:32em;
@media (max-width: 48em) {
    background-color:var(--p-dark);
    flex-direction:column;
    margin-left:0;
    margin-bot:5em;
    border-bottom: 2px var(--p-vlight) solid;
    width:100%;
    max-width:100%;
    ${props => !props.show && css`
        display:none;
    `}
}
`
const Left = styled.div`
    width:50%;
    max-width:32em;
    display:flex;
    justify-content:space-between;
    @media (max-width: 48em) {
        width:100%;
        max-width:100%;
    }
`

const Icon = styled.a`
    color:var(--p-vlight);
    display:none;
    text-align: center;
    transition: all 0.3s ease;
    text-decoration: none;
    padding:var(--margin);
    margin:0;
    @media (max-width: 48em) {
        display:inline;
    }
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
    }
    ${props => props.transparent && css`
        color: var(--p-vdark);
    `}
`

const Button = styled.button`
    border: none;
    min-width:100px;
    text-align: center;
    text-decoration: none;
    width:auto;
    border-radius: .5em;
    padding:.8em;
    color: var(--p-vlight);
    background-color: transparent;
    :hover{
        color: var(--s-5);
        transition:1.5s;
        -webkit-transition:1.5s;
        border-radius:.3em;
    }
    @media (max-width: 48em) {
        width:100%;
    }
    ${props => props.transparent && css`
        color: var(--p-vdark);
    `}
`

const Menu = styled.div`
    z-index:750;
    position:fixed;
    top:0;
    right:-100vw;
    height:100vh;
    width:100%;
    padding:var(--margin);
    background-color:var(--p-3);   
    transition: .3s; 
    ${props => props.show && css`
        right:0vw;
        display:flex;
        flex-direction:column;
        justify-content:center;
    `}
    @media (min-width: 48em) {
        display:none;
    }
`

const Cancel = styled.a`
    position:absolute;
    top:12px;
    right:16px;
    color: var(--p-vlight);
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
    }
`

export default function Navbar (props) {
    const [listItems, setListItems] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [clicked, setClicked] = useState(false);
    const [themeChanger, setThemeChanger] = useState(<Brightness2Icon/>);
    const [transparent, setTransparent] = useState(true);

    const changeTransparent = () =>{
        if(window.scrollY >= 60){
            setTransparent(false);
        } else {
            setTransparent(true);  
        }
    }

    useEffect( () => {

        window.addEventListener("scroll", changeTransparent);
        const current = localStorage.getItem('theme');
        if(current==='dark'){
            setThemeChanger(<WbSunnyIcon/>)
        } else if(current==='light'){
            setThemeChanger(<Brightness2Icon/>)
        }

        retrieveNavbar()
        .then(res => {
            if(res.items.type === 'undefined'){

            } else {
                const Items = res.items.map((item) =>
                    <AnchorButton transparent={transparent} key={item.title} href={item.url}>{item.title}</AnchorButton>
                );
                setListItems(Items)
                setLoaded(true);
            }
        });
        return () => {
            setLoaded(false);
            window.removeEventListener("scroll", changeTransparent);
        }
    },[transparent])

    const showLinks = () => {
        console.log(!clicked);
        setClicked(!clicked)
    }

    const changeTheme = () => {
        if(props.theme==='dark'){
          props.setTheme('light')
          localStorage.setItem('theme', 'light');
          setThemeChanger(<Brightness2Icon/>)
        } else {
          props.setTheme('dark')
          localStorage.setItem('theme', 'dark');
          setThemeChanger(<WbSunnyIcon/>)
        }
    }

    if(loaded){
        return (
                <TopNav transparent={transparent}>
                    <Left>
                        <LogoButton transparent={transparent} mr href='/'>Milan Tornier</LogoButton>
                        <Icon transparent={transparent} onClick={showLinks}><ReorderIcon/></Icon>
                    </Left>
                    <Right>
                        {listItems}
                        <AnchorButton onClick={changeTheme}> {themeChanger} </AnchorButton>
                    </Right>
                    <Menu show={clicked}>
                        {listItems}
                        <Icon onClick={showLinks}><Close/></Icon>
                        <AnchorButton onClick={changeTheme}> {themeChanger} </AnchorButton>
                    </Menu>
                </TopNav>
        )
    }
    else {
        return null;
    }
}
