import React, {useEffect, useState} from 'react'
import {retrieveIG} from '../../service/IGService';
import {Wrapper, Spinner} from '../Styled'
import styled from "styled-components";


const ImageGrid = styled.div`
    display:grid;
    grid-templat-columns: repeat(3, 1fr);
`

const ImageContainer = styled.img`
    width:33%;
`

export default function InstaPreview () {
    const [posts, setPosts] = useState()
    const [isLoaded, setIsLoaded] = useState()

    useEffect( () => {   
        retrieveIG()
        .then(res => {
            setPosts(res)
            setIsLoaded(true)
        })
        return function cleanup() {
            setIsLoaded(false);
        }
    },[])


        if(isLoaded){
            return (
                <Wrapper id="ig">
                    <ImageGrid>
                        { posts.map(post => (
                            <ImageContainer src={post.media_url} alt=""/>
                        ))}
                    </ImageGrid>
                </Wrapper>
            )
        }
        else{
            return (
                <Wrapper id="ig">
                    <Spinner/>
                </Wrapper>
            )
        }
}
