import React, { useState, useEffect } from 'react';
import {Link} from "react-router-dom";

import { retrieveFeaturedMedia, retrieveAuthor } from '../../../service/WPService';
import styled from "styled-components";
import {Title} from '../../Styled';


const Item = styled.section`
    height:25em;
    max-height:100%;
    width:100%;
    max-width:14em;
    background-color:var(--p-1);
    border-radius:1em;
    padding:.5em;
    -webkit-box-shadow: 2px 5px 10px 0px var(--p-vdark);
    -moz-box-shadow: 2px 5px 10px 0px var(--p-vdark);
    box-shadow: 2px 5px 10px 0px var(--p-vdark);
`

const ItemTitle = styled(Title)`
    padding:0 .5em;
    font-size:1.5em;
    color: var(--p-9);
    ::after, ::before{
        background-color:var(--p-9);
    }
`

const Info = styled.div`
    padding:1em 0;
    height:50%;
    display:flex;
    flex-direction:column;
    justify-content:space-between;
    align-items:center;
`

const Image = styled.img`
    width:100%;
    height:50%;
    object-fit: cover;
    display:block; 
    border-radius:.5em;
`

const Buttons = styled.div`
    display:flex;
    width:100%;
`

const PrimaryButton = styled(Link)`
    background-color: var(--s-3);
    color: var(--p-10);
    display: inline-block;
    text-align: center;
    text-decoration: none;
    border:2px solid var(--s-3);
    border-radius:1em;
    width: 90%;
    padding:.5em;
    margin:0 .5em;
    :hover{
        background-color: transparent;
        border:2px solid var(--p-9);
        color: var(--p-9);
        transition: .5s;
    }
`

const SecondaryButton = styled(Link)`
    background-color: var(--s-3);
    color: var(--p-10);
    display: inline-block;
    text-align: center;
    text-decoration: none;
    border:2px solid var(--s-3);
    border-radius:1em;
    width: 90%;
    padding:.5em;
    margin:0 .5em;
    :hover{
        background-color: transparent;
        border:2px solid var(--p-9);
        color: var(--p-9);
        transition: .5s;
    }
`

export default function PostSliderItem(props)  {
    const [post] = useState(props.post);
    const [imageUrl, setImageUrl] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect( () => {
        console.log(post)
        const getImageUrl = retrieveFeaturedMedia(post.featured_media)
        const getAuthor = retrieveAuthor(post.author)
        Promise.all([getImageUrl, getAuthor]).then(res => {
            if(!(res[0] === null)){
                setImageUrl(res[0].media_details.sizes.full.source_url)
            }
            setIsLoaded(true)
        });
    },[post])

    if(isLoaded){
        return (
            <Item>
                <Image src={imageUrl} alt={post.title.rendered}/>
                <Info>
                    <ItemTitle dangerouslySetInnerHTML={{__html: post.title.rendered}}/> 
                    <Buttons>
                        <PrimaryButton to={{
                            pathname: `/post/${post.id}`
                        }}>
                            Excerpt.
                        </PrimaryButton>
                        <SecondaryButton to={{
                            pathname: `/post/${post.id}`
                        }}>
                            Read.
                        </SecondaryButton>
                    </Buttons>
                </Info> 
            </Item>
        )
    }
    else {
        return (null);
    }
}
