import React, { useState, useEffect } from 'react';
import {Link} from "react-router-dom";

import { retrieveFeaturedMedia, retrieveAuthor } from '../../service/WPService';
import styled, { css } from "styled-components";
import {H1} from '../../components/Styled';
import Close from '@material-ui/icons/Close';

const Item = styled.section`
    min-height:25em;
    height:100%;
    width:100%;
    max-width:20em;
    transform-style: preserve-3d;
    transition: all .5s ease-in-out;
    ${props => props.flip && css`
        transform: rotateY(180deg);
    `}
`
const ItemTitle = styled(H1)`
    padding:0 .5em;
    font-size:1.5em;
    color: var(--p-9);
    ::after, ::before{
        background-color:var(--p-9);
    }
`

const Info = styled.div`
    padding:1em 0 0 0;
    height:50%;
    display:flex;
    flex-direction:column;
    justify-content:space-between;
    align-items:center;
`

const Image = styled.img`
    width:100%;
    height:50%;
    object-fit: cover;
    display:block; 
    border-radius:.5em;
`

const PrimaryButton = styled(Link)`
    background-color: var(--s-3);
    color: var(--p-10);
    display: inline-block;
    text-align: center;
    text-decoration: none;
    border:1px solid var(--s-3);
    border-radius:1em;
    width: 90%;
    padding:.5em;
    margin:.5em;
    transition: .5s;
    :hover{
        border:1px solid var(--p-9);
        color: var(--p-9);
    }
`

export default function PhotoSliderItem(props)  {
    const [post] = useState(props.post);
    const [imageUrl, setImageUrl] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);
    const [flip, setFlip] = useState(false);


    useEffect( () => {
        const getImageUrl = retrieveFeaturedMedia(post.featured_media)
        const getAuthor = retrieveAuthor(post.author)
        Promise.all([getImageUrl, getAuthor]).then(res => {
            if(!(res[0] === null)){
                setImageUrl(res[0].media_details.sizes.full.source_url)
            }
            setIsLoaded(true)
        });
    },[post])

    if(isLoaded){
        return (
            <Item>
                <Image src={imageUrl} alt={post.title.rendered}/>
                <Info>
                    <ItemTitle dangerouslySetInnerHTML={{__html: post.title.rendered}}/> 
                    <PrimaryButton to={{
                        pathname: `/post/${post.id}`
                    }}>
                        read
                    </PrimaryButton>  
                </Info> 
            </Item>
        )
    }
    else {
        return (null);
    }
}
