import React from 'react'
import styled, {keyframes, css} from "styled-components";

const Container = styled.div`
    height:14em;
    width:14em;
    position:relative;
    display:flex;
    flex-direction:column;
    overflow:hidden;

    font-size:20px;

    --delay0: 0s;
    --delay1: 1s;
    --delay2: 1.4s;
    --delay3: 1.8s;
    --delay4: 2.2s;
    --delay5: 2.6s;

    --a:.4em;
    --b:.7em;
    --c:.8em;

`

const Row = styled.div`
    display:flex;

    position:relative;
`
const Row1 = styled(Row)`
`
const Row2 = styled(Row)`
    transform: translateX(calc(-1 * var(--b)));
    width:calc(22 * var(--b))
`

const Box = styled.div`
    height:calc(var(--c));
    width: calc(2 * var(--b)); 
    position:relative;
    top:0;
    left:0;
    background: 
        linear-gradient(90deg,
        var(--p-5) 0%, 
        var(--p-3) 130%);
    transform:scale(.6);
    ::after { 
        content: ""; 
        position: absolute; 
        bottom: calc(-1 * var(--a)); 
        left: 0; 
        width: 0; 
        height: 0; 
        border-left: var(--b) solid transparent; 
        border-right: var(--b) solid transparent; 
        border-top: var(--a) solid var(--s-3); 
    }
    ::before { 
        content: ""; 
        position: absolute; 
        top: calc(-1 * var(--a)); 
        left: 0; 
        width: 0; 
        height: 0; 
        border-left: var(--b) solid transparent; 
        border-right: var(--b) solid transparent; 
        border-bottom: var(--a) solid var(--p-3); 
    }
`

const Back = styled.div`
    background-color:white;
    padding-bottom:calc(1/2 * var(--a));
`

export default function Glitter() {
    var rows = [];
    let numrows=7;
    for (var i = 0; i < numrows; i++) {
        rows.push(
        <Row1>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
        </Row1>,
        <Row2>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
            <Back>
                <Box/>
            </Back>
        </Row2>
        );
    }

    return (
        <Container>
            {rows}
        </Container>
        )
}
