import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
import { retrieveFeaturedMedia, retrievePageByName } from '../../service/WPService';
import {Spinner, Loading} from '../Styled'

const Wrapper = styled.div`
    min-height:100vh;
    display:flex;
    justify-content:center;
    align-items:center;
    padding:calc(var(--navbar-height) + var(--margin)) 0;
`

const WelcomeContainer = styled.section`
    border: 2px solid black;
    width:100%;
    max-width:60em;
    align-items:center;
    display:flex;
    flex-direction:column;
    margin:var(--margin);
    @media (max-width: 45em) {
        max-width:30em;
        height:auto;
    }
`

const Upper = styled.div`
    display:flex;
    flex-direction:column;
    height:auto;
`

const Image = styled.img`
    object-fit: cover;
    display:block;
    width:100%;
    height:100%;
`

const Lower = styled.div`
    display:flex;  
    flex-direction:column;
    height:auto;
`
const Social = styled.div`
`

const TextContainer = styled.div`

`

const Title = styled.h1` 
    margin:var(--margin);
    text-align:center;
    color:var(--p-1);
`

const Text = styled.div`
    p{
        color:var(--p-1);
    }
    text-align:justify;
`

export default function Welcome() {

    const [welcome, setWelcome] = useState()
    const [isLoaded, setIsLoaded] = useState(false)
    const [imageUrl, setImageUrl] = useState('');

    const setStuff = async () => {
        const page = await retrievePageByName("Welcome");
        console.log(page)
        setWelcome(page)
        if(page===null){
            return;
        }
        retrieveFeaturedMedia(page.featured_media)
        .then(res => {
            setImageUrl(res.media_details.sizes.full.source_url)
            setIsLoaded(true)
        })
    }

    useEffect( () => {   
        setStuff()

        return function cleanup() {
            setIsLoaded(false);
        }
    },[])

    if(isLoaded){
        return (
            <Wrapper id="about">
                <WelcomeContainer>
                    <Upper>
                        <Image src={imageUrl}/>
                        <Title dangerouslySetInnerHTML={{__html: welcome.title.rendered}}/>
                    </Upper>
                    <Lower>
                        <Social>
                            <li>
                                Ig
                            </li> 
                            <li>
                                In
                            </li>
                        </Social>
                        <TextContainer>
                            <Text dangerouslySetInnerHTML={{__html: welcome.content.rendered}}/>
                        </TextContainer>
                    </Lower>
                </WelcomeContainer>
            </Wrapper>
        )
    }
    else{
        return (
            <Loading id="about">
                <Spinner/>
            </Loading>
        )
    }
}
