import React, {useState} from 'react'
import {Wrapper, H1} from '../Styled';
import Input from './Input'

import styled from "styled-components";

const Container = styled(Wrapper)`
    min-height:80vh;
`

const Form = styled.form`
    border: 2px solid var(--s-3);
    border-radius:.5em;
    padding:1em;
    width:40em;
    margin:0 auto;
    display:flex;
    flex-direction:column;
    align-items:center;
`

const ValidationHints = styled.div`
    p{
        margin:0 1em;
        background-color:var(--s-3);
        color:var(--p-10);
        border-radius:0 0 .3em .3em;
        padding:.2em;
    }
`

export default function Contact() {
    const [name, setName] = useState('')
    const [eMail, setEMail] = useState('')
    const [message, setMessage] = useState('')

    return (
        <Container>
            <H1>Contact</H1>
            <Form>
                <Input label='Name' value={name} setValue={(name) => setName(name)} />
                <ValidationHints>name can be empty</ValidationHints>
                <Input label='E-Mail' value={eMail} setValue={(name) => setEMail(name)} />
                <Input label='Message' value={message} setValue={(name) => setMessage(name)} />
                <button></button>
            </Form>
        </Container>
    )
}
