import {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios';


const Form = styled.form`
display:flex;
flex-direction:column;
`

export default function CommentForm(props) {


    const [name, setName] = useState()
    const [post] = useState(this.props.post.id)
    const [email, setEmail] = useState()
    const [comment, setComment] = useState()

    const handleSubmit = (event) =>{
        event.preventDefault();

        const data = JSON.stringify({
            post: post,
            author_name: name,
            author_email: email,
            content:comment,
          });

          console.log(data);
        axios({
            method: 'post',
            url: `/wp-json/wp/v2/comments`,
            headers: {'Content-Type': 'application/json'}, 
            data: data
        })
        .then(res => console.log(res))
        .catch(err => console.error(err))
      }

        return (
            <Form onSubmit={this.handleSubmit.bind(this)}>
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" value={name} onChange={(event) => {setName(event.target.value)}}/>
                <label htmlFor="email">Email:</label>
                <input type="text" id="email" name="email" value={email} onChange={(event) => {setEmail(event.target.value)}}/>
                <label htmlFor="comment">Comment:</label>
                <input type="text" id="comment" name="comment" value={comment} onChange={(event) => {setComment(event.target.value)}}/>
                <button type="submit">submit</button>
            </Form>
        );
    }
