import React from 'react'
import styled, {keyframes, css} from "styled-components";

const Container = styled.div`
    height:800px;
    width:800px;
    position:relative;
    font-size:100px;
    --X:133px;
    --Y:35px;
    background-color:white;

    --delay1: 0s;
    --delay2: 1s;
    --delay3: 1.2s;
    --delay4: 1.4s;
    --delay5: 1.6s;
`

const fadeInBox = keyframes`
    0% {
        opacity: .5;
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
    100% {
        opacity: 1;
    }
`;
const boxTransform1 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;

const boxTransform2 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;

const boxTransform3 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;

const boxTransform4 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;

const boxTransform5 = keyframes`
    100% {
        transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    }
`;


const fadeInRow = keyframes`
    0% {
        transform:rotateZ(0deg) translateY(var(--Y)) translateX(var(--X));
    }
    100% {
    }
`;

const Box = styled.div`
    opacity: 0;
    height:.99em;
    width:.99em;
    position:absolute;
    border:solid 1px black;
    background-color:${props => props.color};
    background: linear-gradient(90deg,
                                ${props => props.color} 0%, 
                                var(--p-3) 130%);
    ${props => props.order >= 1 && css`
        animation:${boxTransform1} 1s ease-in var(${props => props.delay}) forwards;
    `}
    ${props => props.order >= 2 && css`
        animation:${boxTransform2} 1s ease-in var(${props => props.delay}) forwards;
    `}
    ${props => props.order >= 3 && css`
        animation:${boxTransform3} 1s ease-in var(${props => props.delay}) forwards;
    `}
    ${props => props.order >= 4 && css`
        animation:${boxTransform4} 1s ease-in var(${props => props.delay}) forwards;
    `}
    ${props => props.order >= 5 && css`
        animation:${boxTransform5} 1s ease-in var(${props => props.delay}) forwards;
    `}
`

const Box1 = styled(Box)`
    opacity: .5;
    transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
`

const Box2 = styled(Box)`
    transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
`

const Box3 = styled(Box)`
`

const Box4 = styled(Box)`
    transform:translateY(.92em) translateX(-.24em) skew(-30deg) rotateX(-30deg);
`
const Box5 = styled(Box)`
    transform:translateY(1.59em) translateX(-.91em) skew(-60deg) rotateX(-60deg);
`

const Row = styled.div`
    top: 50%;
    left: 50%;
    position: absolute;
    animation:${fadeInRow} 1s ease-out forwards;
`
const Row1 = styled(Row)`
    transform:rotateZ(0deg) translateY(3em) translateX(3em);
`
const Row2 = styled(Row)`

    transform:rotateZ(-60deg) translateY(var(--Y)) translateX(var(--X));
`

const Row3 = styled(Row)`

    transform:rotateZ(-120deg) translateY(var(--Y)) translateX(var(--X));
`

const Row4 = styled(Row)`

    transform:rotateZ(-180deg) translateY(var(--Y)) translateX(var(--X));
`

const Row5 = styled(Row)`

    transform:rotateZ(-240deg) translateY(var(--Y)) translateX(var(--X));
`

const Row6 = styled(Row)`

    transform:rotateZ(-300deg) translateY(var(--Y)) translateX(var(--X));
`

export default function Figure() {
    return (
        <Container>
            <Row1>
                <Box1 color="var(--s-5)" delay="--delay1" order="1" />
                <Box2 color="var(--s-5)" delay="--delay2" order="2"/>
                <Box3 color="var(--s-5)" delay="--delay3"/>
                <Box4 color="var(--s-5)" delay="--delay4"/>
                <Box5 color="var(--s-5)" delay="--delay5"/>
            </Row1>
            <Row2>
                <Box1 color="var(--s-7)" delay="--delay1"/>
                <Box2 color="var(--s-7)" delay="--delay2"/>
                <Box3 color="var(--s-7)" delay="--delay3"/>
                <Box4 color="var(--s-7)" delay="--delay4"/>
                <Box5 color="var(--s-7)" delay="--delay5"/>
            </Row2>
            <Row3>
                <Box1 color="var(--s-5)" delay="--delay1"/>
                <Box2 color="var(--s-5)" delay="--delay2"/>
                <Box3 color="var(--s-5)" delay="--delay3"/>
                <Box4 color="var(--s-5)" delay="--delay4"/>
                <Box5 color="var(--s-5)" delay="--delay5"/>
            </Row3>
            <Row4>
                <Box1 color="var(--s-3)" delay="--delay1"/>
                <Box2 color="var(--s-3)" delay="--delay2"/>
                <Box3 color="var(--s-3)" delay="--delay3"/>
                <Box4 color="var(--s-3)" delay="--delay4"/>
                <Box5 color="var(--s-3)" delay="--delay5"/>
            </Row4>
            <Row5>
                <Box1 color="var(--s-1)" delay="--delay1"/>
                <Box2 color="var(--s-1)" delay="--delay2"/>
                <Box3 color="var(--s-1)" delay="--delay3"/>
                <Box4 color="var(--s-1)" delay="--delay4"/>
                <Box5 color="var(--s-1)" delay="--delay5"/>
            </Row5>
            <Row6>
                <Box1 color="var(--s-3)" delay="--delay1"/>
                <Box2 color="var(--s-3)" delay="--delay2"/>
                <Box3 color="var(--s-3)" delay="--delay3"/>
                <Box4 color="var(--s-3)" delay="--delay4"/>
                <Box5 color="var(--s-3)" delay="--delay5"/>
            </Row6>
        </Container>
    )
}
