import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
import { retrieveFeaturedMedia, retrieveAboutPage } from '../service/WPService';

const AboutContainer = styled.section`
    Display:flex;
    height: calc(100vh - 8em);
    margin:5em 1em 1em 0em;
    background: linear-gradient(90deg, transparent 75%, var(--p-light) 10em);
    @media (max-width: 45em) {
        min-height: calc(100vh - 6em);
        height:auto;
        background:none;
        flex-direction:column;
        margin:5em 0 0 0;
    }
`

const ImageContainer = styled.section`
    padding:1em;
    display:flex;
    align-items:center;
    justify-content:center;
    height:100%;
    width:50%;
    margin-left:2em;
    @media (max-width: 45em) {
        background: linear-gradient(90deg, transparent 50%, var(--p-light) 8em);
        justify-content:center;
        width:100%;
        height:40%;
        margin-left:0em;
    }
`

const TextContainer = styled.section`
    height:100%;
    width:50%;
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    margin-left:1em;
    @media (max-width: 45em) {
        width:100%;
        max-width: none;
        height:50%;
        order 2;
        margin-left:0;
        margin-top:1em;
    }
`

const Image = styled.div`
    width:90%;
    height:90%;
    max-height:24em;
    max-width:24em;
    border-radius:50%;
    background-color:black;
    @media (max-width: 45em) {
        height:16em;
        width:16em;
    }
`

const Title = styled.h1`
`

const Text = styled.p`
    max-width: 30em;
    text-align:left;
    @media (max-width: 45em) {
        text-align:justify;
    }
`


export default function About() {

    const [about, setAbout] = useState()
    const [isLoaded, setIsLoaded] = useState(false)
    const [imageUrl, setImageUrl] = useState('');

    useEffect( () => {

        const about = retrieveAboutPage()
        setAbout(about);
        if(about){
            console.log('res')
            console.log(res)
            setAbout(res)
            retrieveFeaturedMedia(res.featured_media)
        .then(res => {
            setImageUrl(res.media_details.sizes.full.source_url)
            setIsLoaded(true)
        })
        }
    },[])

    return (
        <AboutContainer>
            <TextContainer>
                <Title>
                    About
                </Title>
                <Text>
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. 
                    Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, 
                    when an unknown printer took a galley of type and scrambled it to make a type specimen book. 
                    It has survived not only five centuries, 
                    but also the leap into electronic typesetting, 
                    remaining essentially unchanged. 
                    It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, 
                    and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.
                </Text>
            </TextContainer>
            <ImageContainer>
                <Image src={imageUrl}/>
            </ImageContainer>
        </AboutContainer>
    )
}
