import React from 'react'
import styled, {keyframes, css} from "styled-components";

const Container = styled.div`
    height:6em;
    width:6em;
    position:relative;
    font-size:50px;

    --delay0: 0s;
    --delay1: 1s;
    --delay2: 1.4s;
    --delay3: 1.8s;
    --delay4: 2.2s;
    --delay5: 2.6s;

    --a:.4em;
    --b:.7em;
    --c:.8em;

`

const Row = styled.div`
    display:flex;
`

const Box = styled.div`
    height:calc(var(--c));
    width:calc(2*var(--a));
    background: 
        linear-gradient(90deg,
        ${props => props.color} 0%, 
        var(--p-3) 130%);
`

export default function Glitter() {
    return (
        <Container>
            <Row>
                <Box/>
                <Box/>
                <Box/>
                <Box/>
                <Box/>
            </Row>
        </Container>
        )
}
