import React, {useState} from 'react'
import styled, {css} from 'styled-components'
import CommentForm from './CommentForm'

const Comment = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:1em;
    margin:1em;
    display:flex;
    max-width:55em;
    justify-content:space-between;
`

const Replies = styled.div`
    ${props => props.hideReplies && css`
        display:none;
    `}
`




export default function CommentItem (props) {
    const [hideReplies, setHideReplies] = useState(true);
    const [hideForm, sethideForm] = useState(true);

    const showReplies = () => {
        setHideReplies(!hideReplies);
    }

    const showForm = () => {
        sethideForm(!hideForm);
        console.log(hideForm)
    }

    return (
        <div>
            <Comment dangerouslySetInnerHTML={{__html: props.comment.content.rendered}}>
            </Comment>
            <button onClick={() => showReplies()}>Show replies</button>
            <button onClick={() => showForm()}>reply</button>
            <Replies hideReplies={hideReplies} >Replies</Replies>
            {hideForm &&
                <CommentForm post={props.comment.post}></CommentForm>
            }
            }
        </div>
    )
}
