import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
import { retrieveFeaturedMedia, retrievePageByName } from '../../service/WPService';
import {Spinner, Loading} from '../Styled'
import Instagram from '@material-ui/icons/Instagram';
import LinkedIn from '@material-ui/icons/LinkedIn';


const Wrapper = styled.div`
    min-height:100vh;
    display:flex;
    justify-content:center;
    align-items:center;
    padding:calc(var(--navbar-height) + var(--margin)) 0;
`

const WelcomeContainer = styled.section`
    width:100%;
    max-width:60em;
    align-items:center;
    display:flex;
    flex-direction:column;
    margin:var(--margin);
    @media (max-width: 45em) {
        max-width:30em;
        height:auto;
    }
`

const Upper = styled.div`
    display:flex;
    flex-direction:column;
    height:auto;
`

const Image = styled.img`
    padding:calc(var(--margin)/2);
    object-fit: cover;
    display:block;
    width:100%;
    height:100%;
`

const Lower = styled.div`
    display:flex;  
    flex-direction:column;
    height:auto;
`
const Social = styled.div`
    display:flex;
    flex-direction:column;
    ::after{
        content: "";
        height: 2px;
        background-color: #000;
    }
    ::before{
        content: "";
        height: 1px;
        background-color: #000;
    }
`

const SocialList = styled.ul`
    display:flex;
    justify-content:flex-end;
    margin:var(--margin) 0;
`

const SocialItem = styled.li`
    list-style-type: none;
    display:flex;
    align-items:center;
    justify-content:center;
    padding:calc(var(--margin)/2) 0 calc(var(--margin)/2) calc(2*var(--margin));
`

const SocialLink = styled.a`
    text-decoration: none;
    color: var(--p-3);
    :hover{
        transition:.5s;
        -webkit-transition:.5s;
        color: var(--s-3);
    }
`

const Title = styled.h1` 
    margin:calc(var(--margin)/2) calc(var(--margin)/4);
    color:var(--p-1);
    display: flex;
    flex-direction:column;
    ::after{
        content: "";
        margin-top:calc(var(--margin)/4);
        height: 1px;
        background-color: #000;
    }
    ::before{
        content: "";
        margin-bottom:calc(var(--margin)/4);
        height: 2px;
        background-color: #000;
    }
`

const Text = styled.div`
    p{
        color:var(--p-1);
        margin:0 calc(var(--margin)/2);
    }
    text-align:justify;
`

export default function Welcome() {

    const [welcome, setWelcome] = useState()
    const [isLoaded, setIsLoaded] = useState(false)
    const [imageUrl, setImageUrl] = useState('');

    const setStuff = async () => {
        const page = await retrievePageByName("Welcome");
        console.log(page)
        setWelcome(page)
        if(page===null){
            return;
        }
        retrieveFeaturedMedia(page.featured_media)
        .then(res => {
            setImageUrl(res.media_details.sizes.full.source_url)
            setIsLoaded(true)
        })
    }

    useEffect( () => {   
        setStuff()

        return function cleanup() {
            setIsLoaded(false);
        }
    },[])

    if(isLoaded){
        return (
            <Wrapper id="about">
                <WelcomeContainer>
                    <Upper>
                        <Image src={imageUrl}/>
                        <Title dangerouslySetInnerHTML={{__html: welcome.title.rendered}}/>
                    </Upper>
                    <Lower>
                        <Text dangerouslySetInnerHTML={{__html: welcome.content.rendered}}/>
                        <Social>
                            <SocialList>
                                <SocialItem>
                                    <SocialLink href="/"><Instagram/></SocialLink>
                                </SocialItem> 
                                <SocialItem>
                                    <SocialLink href="/"><LinkedIn/></SocialLink>
                                </SocialItem>
                            </SocialList>
                        </Social>
                    </Lower>
                </WelcomeContainer>
            </Wrapper>
        )
    }
    else{
        return (
            <Loading id="about">
                <Spinner/>
            </Loading>
        )
    }
}
