import {useLocation} from "react-router-dom";
import axios from 'axios';


function PostPage(props) {
    const location = useLocation();
    const { featured_media, content } = location.state.post;
    const getImageUrl = axios(`/wp-json/wp/v2/media/${featured_media}`).then(res =>{
        return (
            <div>
                <img href={getImageUrl} alt="alt"/>
                {content.rendered}
            </div>
        );
        })

  }

export default PostPage
