import styled from 'styled-components'

export const Wrapper = styled.section`
    margin-right: auto; /* 1 */
    margin-left:  auto; /* 1 */

    max-width: 60em; /* 2 */

    padding-right: 1em; /* 3 */
    padding-left:  1em; /* 3 */
`

export const Loading = styled.section`  
    margin-right: auto; /* 1 */
    margin-left:  auto; /* 1 */

    max-width: 60em; /* 2 */

    display:flex;
    justify-content:center;
    align-item:center;
`

const Comment = styled.div`
        border: 2px solid black;
        margin:1em;
        display:flex;
        max-width:55em;
        justify-content:space-between;
    `
