import react,{useState} from 'react'
import styled from 'styled-components'
import axios from 'axios';


const Form = styled.form`
display:flex;
flex-direction:column;
`

export default function CommentForm(props) {


    const state = useState =Â {
        post: this.props.post.id,
        name:'',
        email:'',
        comment:'',
    }



    const handleSubmit = (event) =>{
        event.preventDefault();

        const {post, name, email, comment } = state;
        const data = JSON.stringify({
            post: post,
            author_name: name,
            author_email: email,
            content:comment,
          });

          console.log(data);
        axios({
            method: 'post',
            url: `/wp-json/wp/v2/comments`,
            headers: {'Content-Type': 'application/json'}, 
            data: data
        })
        .then(res => console.log(res))
        .catch(err => console.error(err))
      }


        const { name, email, comment } = this.state;

        return (
            <Form onSubmit={this.handleSubmit.bind(this)}>
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" value={name} onChange={(event) => {this.setState({name: event.target.value})}}/>
                <label htmlFor="email">Email:</label>
                <input type="text" id="email" name="email" value={email} onChange={(event) => {this.setState({email: event.target.value})}}/>
                <label htmlFor="comment">Comment:</label>
                <input type="text" id="comment" name="comment" value={comment} onChange={(event) => {this.setState({comment: event.target.value})}}/>
                <button type="submit">submit</button>
            </Form>
        );
    }
