import React, {useState} from 'react'
import styled, {css} from 'styled-components'
import CommentForm from './CommentForm'

export const Comment = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:1em;
    margin:1em;
    display:flex;
    max-width:55em;
    justify-content:space-between;
`

export const Replies = styled.div`
    ${props => props.hideReplies && css`
        display:none;
    `}
`

export default function CommentItem (props) {
    const [hideReplies, setHideReplies] = useState(true);

    const showReplies = (event) => {
        setHideReplies(!hideReplies);
        console.log(hideReplies)
    }

    return (
        <div>
            <Comment dangerouslySetInnerHTML={{__html: props.comment.content.rendered}}>
            </Comment>
            <button onClick={() => showReplies()}>Show replies</button>
            <button onClick={() => showReplies()}>reply</button>
            <Replies hideReplies={hideReplies} >Replies</Replies>
            <CommentForm hideReplies={hideReplies} >Replies</CommentForm>

        </div>
    )
}
