import React, { useState, useEffect } from 'react';
import { retrievePostsByCategoryId } from '../../../service/WPService';
// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';
import styled from "styled-components";
import {Wrapper, Loading, Spinner} from '../../Styled';
import PostSliderItem from './PostSliderItem'

// Import Swiper styles
import 'swiper/swiper-bundle.css';
import 'swiper/components/navigation';
import 'swiper/components/pagination';


// import Swiper core and required modules
import SwiperCore, {
  Navigation, Pagination
} from 'swiper';

SwiperCore.use([Navigation, Pagination]);

const SwiperWrapper = styled(Wrapper)`
  display:flex;
  flex-direction:column;
  background: linear-gradient(180deg ,var(--p-7) 0% ,var(--p-7) 50% ,var(--s-7) 100% );

  .swiper-container{
    .swiper-button-prev{
      color:var(--s-3);
      outline:none;
    }
    .swiper-button-next{
      color:var(--s-3);
      outline:none;

    }
    .swiper-pagination-bullet{
      background-color:var(--s-3);
    }
    .swiper-pagination-bullet-active{
      background-color:var(--s-3);
    } 
  }
  .swiper-slide{
    margin:1em 0;
    padding:1em 0;
    display:flex;
    justify-content:center;
  }
  @media (max-width: 640px) {
    max-width: 640px;
    width:100%;
  }
  @media (max-width: 768px) {
    max-width: 768px;
  }
`

const SwiperContainer = styled.div`

`

export default function PostSlider (props) {

  const [posts, setPosts] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect( () => {

    retrievePostsByCategory(props.category)
    .then(res => {
        setPosts(res.slice(0, 5))
        setIsLoaded(true)
    })
    .catch(err => console.log(err));
  },[])

  if(isLoaded){
    return (
      <SwiperWrapper>
        <SwiperContainer>
          <Swiper
            grabCursor={true}  
            modules={[Navigation, Pagination]}
            navigation
            pagination={{
              "clickable": true,
              "dynamicBullets": true
            }}
            slidesPerView={1}
            breakpoints={{
              "480": {
                "slidesPerView": 1,
                "spaceBetween": 0
              },
              "800": {
                "slidesPerView": 2,
                "spaceBetween": 0
              },
              "1000": {
                "slidesPerView": 3,
                "spaceBetween": 0
              }
            }}
            onSlideChange={() => console.log('slide change')}
            onSwiper={(swiper) => console.log(swiper)}
          >
            { posts.map(post => (
              <SwiperSlide key={'s'+post.id}>
                <PostSliderItem key={post.id} post={post}/>
              </SwiperSlide>
            ))}
          </Swiper>
      </SwiperContainer>
    </SwiperWrapper>
    )
  } else {
  return(               
      <Loading><Spinner/></Loading>
  )
  }
};
