import React, { useState, useEffect } from 'react';
import {retrieveNavbar} from '../../service/WPService';
import styled, {css} from 'styled-components'
import ReorderIcon from '@material-ui/icons/Reorder'

const TopNav = styled.div`
    --nav-size: 100%;
    display:flex;
    height:var(--nav-size);
    background-color:var(--primary);
    @media (max-width: 42em) {
        flex-direction:column;
    }

`
const NavItem = styled.li`
    list-style-type: none;
    display:flex;
    align-items:center;
    justify-content:center;
    transition: all 0.3s ease;
    margin:.2em;
    @media (max-width: 42em) {
        width:auto;
    }
`

const NavLink = styled.a`
    min-width:100px;
    text-align: center;
    text-decoration: none;
    width:auto;
    border-radius: .5em;
    padding:.8em;
    color: white;
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
        background-color:var(--p-dark);
        border-radius:.3em;
    }
    @media (max-width: 42em) {
        width:100%;
    }
`
const Logo = styled(NavLink)`
    margin-right: auto;
    @media (max-width: 42em) {
        width:8em;
    }
`

const Right = styled.div`
display:flex;
flex-direction:row;
margin-left:auto;
background-color:var(--primary);

@media (max-width: 42em) {
    flex-direction:column;
    margin-left:0;
    margin-bot:5em;
    ${props => !props.show && css`
    display:none;
`}
}
`
const Left = styled.div`
    display:flex;
    justify-content:space-between;
`

const Burger = styled.a`
    color:white;
    display:none;
    text-align: center;
    transition: all 0.3s ease;
    text-decoration: none;
    margin:.2em;
    padding:.8em;
    @media (max-width: 42em) {
        display:inline;
    }
    :hover{
        color: var(--s);
        transition:1s;
        -webkit-transition:1s;
    }
`
export default function Navbar () {
    const [listItems, setListItems] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [clicked, setClicked] = useState(false);
    const [hidden, setHidden] = useState(false);
    const [scrollPos, setScrollPos] = useState(window.pageYOffset);

    useEffect( () => {
        window.addEventListener("scroll", this.handleScroll);

        retrieveNavbar()
        .then(res => {
            const Items = res.items.map((item) =>
            <NavItem key={item.title}>
                <NavLink key={item.title} href={item.url}>{item.title}</NavLink>
            </NavItem>
            );
            setLoaded(true);
            setListItems(Items)
        });

        return function cleanup() {
            setLoaded(false);
            window.removeEventListener("scroll", this.handleScroll);

        }
    },[])

    const showLinks = () => {
        let show = clicked;
        setClicked(!show)
    }

    const handleScroll = () => {      
        const currentScrollPos = window.pageYOffset;
        const visible = scrollPos > currentScrollPos;
        setScrollPos(currentScrollPos);
        setHidden(visible);
      };

    if(loaded){
        return (
                <TopNav hidden={hidden}>
                    <Left>
                        <NavItem>
                            <Logo mr href='/'>Logo</Logo>
                        </NavItem>
                        <Burger onClick={showLinks}><ReorderIcon/></Burger>
                    </Left>
                    <Right show={clicked}>
                        {listItems}
                    </Right>
                </TopNav>
        )
    }
    else {
        return null;
    }
}
