import React, {useState, useEffect} from 'react'
import Sidebar from '../Nav/Sidebar'
import Lebenslauf from '../components/Resume/Lebenslauf'
import Vorstellung from '../components/Resume/Vorstellung'

import styled from "styled-components";

const Page = styled.div`
    @media (min-width: 48em) {
        max-width:80em;
    }   
    @media (min-width: 64em) {
        margin-left:auto;
        margin-right:auto;
    }
`

const Sections = styled.div`
    margin:0;
    @media (min-width: 48em) {
        margin-left:calc(var(--sidebar-width));
        margin-right:calc(var(--sidebar-width));
    }  
    @media (min-width: 64em) {
    }      
`

export default function Resume() {
    const [current, setCurrent] = useState([]);
    const [animateVorstellung, setAnimateVorstellung] = useState([false]);
    const [animateLebenslauf, setAnimateLebenslauf] = useState([false]);

    const data=[
        {
            id:'vorstellung',
            label:'Vorstellung',
        },
        {
            id:'lebenslauf',
            label:'Lebenslauf',
        },
    ]

    useEffect( () => {
        let sections = [];
        data.forEach(element => {
            let section = document.getElementById(element.id);
            sections = [...sections ,section];
        });

        const triggerAnimation = () => {
            sections.forEach((section) => {
                const sectionTop = section.offsetTop;
                const sectionHeight = section.clientHeight;
                if (window.pageYOffset >= sectionTop - sectionHeight / 3 && window.pageYOffset < sectionTop + 2*sectionHeight/3 ) {
                    setCurrent(section.getAttribute("id"));
                    if(current==="vorstellung"){
                        setAnimateVorstellung(true);
                    }
                    if(current==="lebenslauf"){
                        setAnimateLebenslauf(true);
                    }
                }
            }); 
        }

        window.addEventListener("scroll", triggerAnimation); 
        return () => {
            window.removeEventListener("scroll", triggerAnimation )
        }
    }, [data])

    return (
        <Page>
            <Sections>
                <div id='vorstellung'><Vorstellung animate={animateVorstellung}/></div>
                <div id='lebenslauf'><Lebenslauf animate={animateLebenslauf}/></div>
            </Sections>
            <Sidebar data={data}/>
        </Page>
    )
}
