import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import "../App.css";
import {Link} from "react-router-dom";
import styled from "styled-components";

const Item = styled.div`
`
const Square = styled.div`
`
const Info = styled.div`
`
const ImageContainer = styled.div`
    max-width: 500px;
    transform: translate(-5em, 0em);
`
const Image = styled.img`
    width:20em;
    height:25em;
    object-fit: cover;
    display:block;
`



export class PostItem extends Component {

    state =Â {
        imageUrl: '',
        author: '',
        isLoaded: false
    }

    static propTypes = {
        post: PropTypes.object.isRequired,
    }

    componentDidMount(){
        const { featured_media, author } = this.props.post;
        const getImageUrl = axios(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios(`/wp-json/wp/v2/users/${author}`);
        Promise.all([getImageUrl, getAuthor]).then(res => {
            this.setState({
                imageUrl: res[0].data.media_details.sizes.full.source_url,
                author: res[1].data.name,
                isLoaded: true
            })
        });
    }

    render() {
        const { title, excerpt}  = this.props.post;
        const { imageUrl, isLoaded} = this.state;
        if(isLoaded){
            return (
                <Item>
                    <div class="square"></div> 
                    <ImageContainer>
                        <Image src={imageUrl} alt={title.rendered}/>
                    </ImageContainer>
                    <div class="info">
                        <div class="title">
                            <h5>{title.rendered}</h5>
                        </div>
                        <div class="excerpt">
                            <div dangerouslySetInnerHTML={{__html: excerpt.rendered }}/>
                        </div>
                        <Link to={{
                            pathname: "/post",
                            state:{
                                post: this.props.post
                            }
                        }}>
                        read more 
                        </Link>
                    </div>
                </Item>
            )
        }
        return null;
    }
}

export default PostItem
